import { describe, test, expect, beforeEach, jest } from '@jest/globals';
import { get } from '../../../src/toys/2025-03-29/get.js';

describe('get function with path traversal', () => {
  let mockGetData;
  let env;
  const testData = {
    user: {
      name: 'Alice',
      age: 30,
      address: {
        city: 'London',
        zip: 'ABC 123'
      },
      orders: [
        { id: 1, item: 'Book' },
        { id: 2, item: 'Pen' }
      ]
    },
    status: 'active'
  };

  beforeEach(() => {
    // Reset mocks and environment before each test
    mockGetData = jest.fn().mockReturnValue(testData);
    env = new Map([
      ['getData', mockGetData]
    ]);
  });

  test('should return the value for a top-level key', () => {
    expect(get('status', env)).toBe(JSON.stringify('active'));
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return the value for a nested key', () => {
    expect(get('user.name', env)).toBe(JSON.stringify('Alice'));
    expect(get('user.address.city', env)).toBe(JSON.stringify('London'));
    expect(mockGetData).toHaveBeenCalledTimes(2);
  });

  test('should return the value for an array index', () => {
    expect(get('user.orders.0.item', env)).toBe(JSON.stringify('Book'));
    expect(get('user.orders.1.id', env)).toBe(JSON.stringify(2));
    expect(mockGetData).toHaveBeenCalledTimes(2);
  });

  test('should return the full object/array if path points to it', () => {
    expect(get('user.address', env)).toBe(JSON.stringify({ city: 'London', zip: 'ABC 123' }));
    expect(get('user.orders.1', env)).toBe(JSON.stringify({ id: 2, item: 'Pen' }));
    expect(mockGetData).toHaveBeenCalledTimes(2);
  });

  test('should return an error if a path segment does not exist (object)', () => {
    const expectedErrorMessage = `Error: Path segment 'lastName' not found at 'user.lastName'. Available keys/indices: name, age, address, orders`;
    expect(get('user.lastName', env)).toBe(expectedErrorMessage);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return an error if a path segment does not exist (array index)', () => {
    const expectedErrorMessage = `Error: Path segment '2' not found at 'user.orders.2'. Available keys/indices: 0, 1`;
    expect(get('user.orders.2', env)).toBe(expectedErrorMessage);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return an error trying to access property on non-object', () => {
    const expectedErrorMessage = `Error: Cannot access property 'city' on non-object value at path 'user.name'. Value is: "Alice"`;
    expect(get('user.name.city', env)).toBe(expectedErrorMessage);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return an error if getData is not a function', () => {
    env.set('getData', 'not a function');
    expect(get('user.name', env)).toBe("Error: 'getData' function not found in env.");
  });

  test('should return an error if env map is not provided or invalid', () => {
    expect(get('user.name', null)).toBe("Error: 'env' Map with 'get' method is required.");
    expect(get('user.name', {})).toBe("Error: 'env' Map with 'get' method is required."); // Plain object is not a Map
  });

  test('should return an error if getData does not return an object or array', () => {
    mockGetData.mockReturnValue('a string');
    expect(get('anyKey', env)).toBe("Error: 'getData' did not return a valid object or array.");

    mockGetData.mockReturnValue(null);
    expect(get('anyKey', env)).toBe("Error: 'getData' did not return a valid object or array.");

    // Note: Array is now a valid return type from getData
    mockGetData.mockReturnValue([1, 2, 3]); 
    expect(get('0', env)).toBe(JSON.stringify(1)); // Should be able to access array elements

    expect(mockGetData).toHaveBeenCalledTimes(3);
  });

  test('should return an error if getData throws an error', () => {
    const errorMessage = 'Failed to fetch data';
    mockGetData.mockImplementation(() => {
      throw new Error(errorMessage);
    });
    expect(get('user.name', env)).toBe(`Error during data retrieval or path traversal for "user.name": ${errorMessage}`);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  // Test for stringifying error remains similar
  test('should handle non-stringifiable values gracefully at the end of the path', () => {
    const circular = {};
    circular.myself = circular;
    const circularData = { top: { nested: circular } }; 
    mockGetData.mockReturnValue(circularData);
    expect(get('top.nested', env)).toMatch(/^Error stringifying final value at path "top.nested":/);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return undefined for valid path to undefined value', () => {
    const dataWithUndefined = {
      user: {
        settings: {
          theme: undefined
        }
      }
    };
    mockGetData.mockReturnValue(dataWithUndefined);
    expect(get('user.settings.theme', env)).toBe(JSON.stringify(undefined));
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should handle empty input string by returning full object', () => {
    mockGetData.mockReturnValue(testData);
    expect(get('', env)).toBe(JSON.stringify(testData));
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should handle numeric string keys in object', () => {
    const objWithNumericKeys = { "2025": { value: "future" } };
    mockGetData.mockReturnValue(objWithNumericKeys);
    expect(get('2025.value', env)).toBe(JSON.stringify('future'));
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });

  test('should return error when accessing deep property on null', () => {
    const nestedNull = { user: { profile: null } };
    mockGetData.mockReturnValue(nestedNull);
    const expectedMessage = `Error: Cannot access property 'name' on non-object value at path 'user.profile'. Value is: null`;
    expect(get('user.profile.name', env)).toBe(expectedMessage);
    expect(mockGetData).toHaveBeenCalledTimes(1);
  });
});

import { describe, test, expect, beforeEach, jest } from '@jest/globals';
import { setTemporary } from '../../../src/toys/2025-03-29/setTemporary.js';

describe('setTemporary function (getData -> merge -> setData)', () => {
  let mockGetData;
  let mockSetData;
  let initialData; // The object returned by mockGetData
  let env;

  beforeEach(() => {
    // Start with fresh, potentially immutable data for getData
    initialData = Object.freeze({
      existing: 'value',
      // 'temporary' might exist or not initially
    });
    mockGetData = jest.fn().mockReturnValue(initialData);
    mockSetData = jest.fn(); // Mock for setData
    env = new Map([
      ['getData', mockGetData],
      ['setData', mockSetData] // Add setData mock to env
    ]);
  });

  test('should call setData with merged JSON when temporary exists', () => {
    // Modify initialData for this test case before freezing (or create a new one)
    initialData = Object.freeze({
        existing: 'value',
        temporary: { initial: 'data' }
    });
    mockGetData.mockReturnValue(initialData);

    const inputJson = JSON.stringify({ newKey: 'newValue', initial: 'overwritten' });
    const expectedFinalData = {
      existing: 'value',
      temporary: { initial: 'overwritten', newKey: 'newValue' }
    };

    const result = setTemporary(inputJson, env);

    expect(result).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockGetData).toHaveBeenCalledTimes(1);
    expect(mockSetData).toHaveBeenCalledTimes(1);
    // Use expect.objectContaining or toEqual for deep comparison
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData));
    // Ensure the object passed to setData is not the same ref as initialData
    expect(mockSetData.mock.calls[0][0]).not.toBe(initialData);
    // Ensure the temporary object within is also a new reference
    if(initialData.temporary) {
        expect(mockSetData.mock.calls[0][0].temporary).not.toBe(initialData.temporary);
    }
  });

  test('should call setData creating temporary if it does not exist', () => {
    // initialData already lacks temporary from beforeEach
    const inputJson = JSON.stringify({ firstKey: 123 });
    const expectedFinalData = {
        existing: 'value',
        temporary: { firstKey: 123 }
    };

    const result = setTemporary(inputJson, env);

    expect(result).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockGetData).toHaveBeenCalledTimes(1);
    expect(mockSetData).toHaveBeenCalledTimes(1);
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData));
    expect(mockSetData.mock.calls[0][0]).not.toBe(initialData);
  });

  test('should call setData creating temporary if it exists but is not a valid object', () => {
    initialData = Object.freeze({ existing: 'value', temporary: 'a string' });
    mockGetData.mockReturnValue(initialData);
    const inputJson = JSON.stringify({ key: 'val' });
    const expectedFinalData = { existing: 'value', temporary: { key: 'val' } };

    const result = setTemporary(inputJson, env);
    expect(result).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData));
    expect(mockSetData.mock.calls[0][0]).not.toBe(initialData);

    // Try with null
    initialData = Object.freeze({ existing: 'value', temporary: null });
    mockGetData.mockReturnValue(initialData);
    const inputJson2 = JSON.stringify({ another: true });
    const expectedFinalData2 = { existing: 'value', temporary: { another: true } };
    const result2 = setTemporary(inputJson2, env);
    expect(result2).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData2));
    expect(mockSetData.mock.calls[1][0]).not.toBe(initialData);

    // Try with array
    initialData = Object.freeze({ existing: 'value', temporary: [1,2] });
    mockGetData.mockReturnValue(initialData);
    const inputJson3 = JSON.stringify({ third: 3 });
    const expectedFinalData3 = { existing: 'value', temporary: { third: 3 } };
    const result3 = setTemporary(inputJson3, env);
    expect(result3).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData3));
    expect(mockSetData.mock.calls[2][0]).not.toBe(initialData);

    expect(mockGetData).toHaveBeenCalledTimes(3);
    expect(mockSetData).toHaveBeenCalledTimes(3);
  });

  test('should return error for invalid JSON input and not call setData', () => {
    const input = 'not json';
    const result = setTemporary(input, env);
    expect(result).toMatch(/^Error: Invalid JSON input./);
    expect(mockGetData).not.toHaveBeenCalled();
    expect(mockSetData).not.toHaveBeenCalled(); // Verify setData not called
  });

  test('should return error if input JSON is not a plain object and not call setData', () => {
    let input = JSON.stringify([1, 2, 3]); // Array
    expect(setTemporary(input, env)).toBe("Error: Input JSON must be a plain object.");
    expect(mockGetData).not.toHaveBeenCalled();
    expect(mockSetData).not.toHaveBeenCalled();
  });

  test('should return error if getData or setData function is missing in env', () => {
    env.delete('getData');
    expect(setTemporary('{}', env)).toBe("Error: 'getData' function not found in env.");
    env.set('getData', mockGetData).delete('setData'); // Restore getData, remove setData
    expect(setTemporary('{}', env)).toBe("Error: 'setData' function not found in env.");
  });

  test('should return error if getData does not return an object and not call setData', () => {
    mockGetData.mockReturnValue('not an object');
    expect(setTemporary('{}', env)).toBe("Error: 'getData' did not return a valid object.");
    expect(mockSetData).not.toHaveBeenCalled();
  });

  test('should return error if getData throws an error and not call setData', () => {
    const errorMessage = 'Failed to retrieve data';
    mockGetData.mockImplementation(() => { throw new Error(errorMessage); });
    expect(setTemporary('{ "a": 1 }', env)).toBe(`Error updating temporary data: ${errorMessage}`);
    expect(mockSetData).not.toHaveBeenCalled();
  });

  test('should return error if setData throws an error', () => {
      const errorMessage = 'Failed to save data';
      mockSetData.mockImplementation(() => { throw new Error(errorMessage); });
      const inputJson = JSON.stringify({ key: 'value' });
      expect(setTemporary(inputJson, env)).toBe(`Error updating temporary data: ${errorMessage}`);
      expect(mockGetData).toHaveBeenCalledTimes(1); // getData was called
      expect(mockSetData).toHaveBeenCalledTimes(1); // setData was called (and threw)
  });

  test('should deep merge nested objects within temporary', () => {
    initialData = Object.freeze({
      existing: 'value',
      temporary: {
        level1: {
          a: 1,
          b: { c: 2 }
        },
        other: 'abc'
      }
    });
    mockGetData.mockReturnValue(initialData);

    const inputJson = JSON.stringify({
      level1: {
        b: { d: 3 }, // This should merge with existing level1.b, not replace it
        e: 4        // This should be added to level1
      },
      newProp: true // Add a new top-level prop to temporary
    });

    const expectedFinalData = {
      existing: 'value',
      temporary: {
        level1: {
          a: 1,       // Preserved from initial
          b: { c: 2, d: 3 }, // Merged
          e: 4        // Added
        },
        other: 'abc', // Preserved from initial
        newProp: true // Added
      }
    };

    const result = setTemporary(inputJson, env);

    expect(result).toBe('Success: Temporary data deep merged.'); // Updated message
    expect(mockGetData).toHaveBeenCalledTimes(1);
    expect(mockSetData).toHaveBeenCalledTimes(1);
    // Use toEqual for deep comparison of the final object structure
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expectedFinalData)); // Check overall structure
    expect(mockSetData.mock.calls[0][0].temporary).toEqual(expectedFinalData.temporary); // Deep check temporary specifically
    // Check that references were changed (deep clone + merge creates new objects)
    expect(mockSetData.mock.calls[0][0]).not.toBe(initialData);
    expect(mockSetData.mock.calls[0][0].temporary).not.toBe(initialData.temporary);
    expect(mockSetData.mock.calls[0][0].temporary.level1).not.toBe(initialData.temporary.level1);
    expect(mockSetData.mock.calls[0][0].temporary.level1.b).not.toBe(initialData.temporary.level1.b);
  });

  test('should overwrite non-object properties during deep merge', () => {
    initialData = Object.freeze({
      temporary: { a: 1, b: 'string', c: [1, 2] }
    });
    mockGetData.mockReturnValue(initialData);

    const inputJson = JSON.stringify({
      a: { nested: true }, // Overwrite number with object
      b: 2,               // Overwrite string with number
      c: { d: 3 }         // Overwrite array with object
    });

    const expectedTemporary = {
      a: { nested: true },
      b: 2,
      c: { d: 3 }
    };

    setTemporary(inputJson, env);
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining({ temporary: expectedTemporary }));
  });

  test('should handle merging onto an empty initial temporary object', () => {
      initialData = Object.freeze({ existing: 'value', temporary: {} });
      mockGetData.mockReturnValue(initialData);
      const inputJson = JSON.stringify({ level1: { a: 1 } });
      const expectedTemporary = { level1: { a: 1 } };

      setTemporary(inputJson, env);
      expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining({ temporary: expectedTemporary }));
  });

  test('should handle merging onto a non-existent initial temporary object', () => {
      initialData = Object.freeze({ existing: 'value' }); // No temporary key
      mockGetData.mockReturnValue(initialData);
      const inputJson = JSON.stringify({ level1: { a: 1 } });
      const expectedTemporary = { level1: { a: 1 } };

      setTemporary(inputJson, env);
      expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining({ temporary: expectedTemporary }));
  });

  test('should preserve existing temporary data if source is not object', () => {
    initialData = Object.freeze({ existing: 'value', temporary: { key: 'val' } });
    mockGetData.mockReturnValue(initialData);

    const inputJson = JSON.stringify(null); // Valid JSON but not an object

    const result = setTemporary(inputJson, env);
    expect(result).toBe("Error: Input JSON must be a plain object.");
    expect(mockSetData).not.toHaveBeenCalled();
  });

  test('should overwrite non-object temporary with object from input', () => {
    initialData = Object.freeze({ existing: 'value', temporary: 42 });
    mockGetData.mockReturnValue(initialData);
    const inputJson = JSON.stringify({ newKey: 'newVal' });

    const expected = { existing: 'value', temporary: { newKey: 'newVal' } };
    const result = setTemporary(inputJson, env);
    expect(result).toBe("Success: Temporary data deep merged.");
    expect(mockSetData).toHaveBeenCalledWith(expect.objectContaining(expected));
  });
});

import { describe, test, expect, beforeEach } from '@jest/globals';
import { cyberpunkAdventure } from '../../../src/toys/2025-03-30/cyberpunkAdventure.js';

describe('Cyberpunk Text Game', () => {
  let tempData;
  let env;

  beforeEach(() => {
    tempData = {};
    env = new Map([
      ['getRandomNumber', () => 0.5],
      ['getCurrentTime', () => '23:59'],
      ['getData', () => ({ temporary: { CYBE1: tempData } })],
      ['setData', (data) => {
        tempData = { ...tempData, ...data.temporary?.CYBE1 };
      }],
    ]);
  });

  test('initial naming and intro', () => {
    expect(cyberpunkAdventure('Blaze', env)).toMatch(/Welcome, Blaze/);
    expect(cyberpunkAdventure('start', env)).toMatch(/you're in the Neon Market/);
  });

  test('goes to Hacker Den and requires password', () => {
    cyberpunkAdventure('Blaze', env);
    cyberpunkAdventure('start', env);
    expect(cyberpunkAdventure('hacker', env)).toMatch(/requires a password/);
    expect(cyberpunkAdventure('zero', env)).toMatch(/cracked implant/);
    expect(tempData.inventory).toContain('cracked implant');
    expect(tempData.visited).toContain('hacker');
  });

  test('goes to Transport Hub and trades datapad', () => {
    tempData = {
      name: 'Blaze',
      state: 'hub',
      inventory: ['datapad'],
      visited: []
    };
    env.set('getData', () => ({ temporary: { CYBE1: tempData } }));
    expect(cyberpunkAdventure('transport', env)).toMatch(/Trains screech overhead./); 
    expect(cyberpunkAdventure(' ', env)).toMatch(/vendor offers/); 
    expect(cyberpunkAdventure('trade datapad', env)).toMatch(/neural ticket/); 
    expect(tempData.inventory).toContain('neural ticket');
    expect(tempData.inventory).not.toContain('datapad');
    expect(tempData.visited).toContain('transport');
  });

  test('goes to Back Alley and finds stimpack (success)', () => {
    tempData = {
      name: 'Blaze',
      state: 'hub',
      inventory: [],
      visited: []
    };
    env.set('getData', () => ({ temporary: { CYBE1: tempData } }));
    expect(cyberpunkAdventure('alley', env)).toMatch(/shadows move with you./); 
    expect(cyberpunkAdventure(' ', env)).toMatch(/hidden stash: a stimpack/); 
    expect(tempData.inventory).toContain('stimpack');
    expect(tempData.visited).toContain('alley');
  });

  test('unknown input in hub', () => {
    cyberpunkAdventure('Blaze', env);
    cyberpunkAdventure('start', env);
    expect(cyberpunkAdventure('xyz', env)).toMatch(/Unclear direction/);
  });
});
