];
const clubs = ['ðŸƒ‘', 'ðŸƒ’', 'ðŸƒ“', 'ðŸƒ”', 'ðŸƒ•', 'ðŸƒ–', 'ðŸƒ—', 'ðŸƒ˜', 'ðŸƒ™', 'ðŸƒš', 'ðŸƒ›', 'ðŸƒ', 'ðŸƒž'];

const allCards = [...spades, ...hearts, ...diamonds, ...clubs];
let currentCard = allCards[Math.floor(Math.random() * allCards.length)];

const correctElement = document.getElementById('correct');
const incorrectElement = document.getElementById('incorrect');
const totalElement = document.getElementById('total');
const currentCardElement = document.getElementById('current-card');

const higherBtn = document.getElementById('higher-btn');
const lowerBtn = document.getElementById('lower-btn');

function getRandomCard() {
  return allCards[Math.floor(Math.random() * allCards.length)];
}

function updateScoreboard() {
  correctElement.textContent = correctGuesses;
  incorrectElement.textContent = incorrectGuesses;
  totalElement.textContent = totalGuesses;
}

// Function to find the index of a card in its respective suit array
function findCardIndex(card) {
  let index = spades.indexOf(card);
  if (index !== -1) return index;
  index = hearts.indexOf(card);
  if (index !== -1) return index;
  index = diamonds.indexOf(card);
  if (index !== -1) return index;
  index = clubs.indexOf(card);
  if (index !== -1) return index;
  return -1; // Default, in case something goes wrong
}

function checkGuess(isHigher) {
  const newCard = getRandomCard();

  // Find the index in the suit arrays
  const currentIndex = findCardIndex(currentCard);
  const newIndex = findCardIndex(newCard);

  // Check the guess according to the rules
  if (newIndex > currentIndex && isHigher) {
    correctGuesses++;
  } else if (newIndex < currentIndex && !isHigher) {
    correctGuesses++;
  } else {
    incorrectGuesses++;
  }

  totalGuesses++;
  currentCard = newCard;
  currentCardElement.textContent = currentCard;
  updateScoreboard();
}

higherBtn.addEventListener('click', () => checkGuess(true));
lowerBtn.addEventListener('click', () => checkGuess(false));

// Initialize the display
currentCardElement.textContent = currentCard;
updateScoreboard();

/**
 * Coerces input to a boolean value if possible.
 * Returns a string representation of an object with a 'value' property if coercion is successful,
 * or a string representation of an empty object if coercion fails.
 *
 * @param {any} input - The value to coerce to boolean
 * @returns {string} - String representation of object with 'value' property if coercion successful, '{}' if not
 */
export function coerceToBoolean(input) {
  if (typeof input === 'boolean') {
    return `{ value: ${input} }`;
  }
  if (typeof input === 'string') {
    const lower = input.toLowerCase();
    if (lower === 'true') return '{ value: true }';
    if (lower === 'false') return '{ value: false }';
  }
  return '{}';
}

// Markdown marker characters
const ASTERISK_MARKER = '*';
const UNDERSCORE_MARKER = '_';

// Pattern to match special regex characters that need escaping
const REGEX_SPECIAL_CHARS = /[.*+?^${}()|[\]\\]/;

/**
 * Creates a doubled marker (e.g., ** or __) for bold text
 * @param {string} marker - The marker character (* or _)
 * @returns {string} - The doubled marker
 * @private
 */
function createDoubledMarker(marker) {
  return marker.repeat(2);
}

/**
 * Creates a regex pattern part for bold text with a specific marker
 * @param {string} marker - The marker character (* or _)
 * @returns {string} - Regex pattern string for bold with the specified marker
 * @private
 */
function createBoldPatternPart(marker) {
  // Escape marker if it's a special regex character
  const escapedMarker = REGEX_SPECIAL_CHARS.test(marker) ? `\\${marker}` : marker;
  const doubledMarker = createDoubledMarker(escapedMarker);

  // Break the pattern into its constituent parts
  const patternParts = [
    '(?:', // Opening non-capturing group
    doubledMarker, // Opening doubled marker
    '.*?', // Lazy match of content
    doubledMarker, // Closing doubled marker
    ')' // Closing group
  ];

  return patternParts.join('');
}

/**
 * Returns a regex pattern that matches both asterisk and underscore bold markdown
 * @returns {RegExp} - The regex pattern for bold markdown
 * @private
 */
function createBoldPattern() {
  const boldMarkers = [ASTERISK_MARKER, UNDERSCORE_MARKER];
  const patternParts = boldMarkers.map(createBoldPatternPart);

  // Combine with OR operator for alternative matching
  const pattern = patternParts.join('|');

  return new RegExp(pattern, 's');
}

/**
 * Returns a regex pattern that matches text wrapped with a specific marker
 * @param {string} marker - The marker character (* or _)
 * @returns {RegExp} - The regex pattern for the specified marker
 * @private
 */
function createItalicsPattern(marker) {
  // Escape marker if it's a special regex character
  const escapedMarker = REGEX_SPECIAL_CHARS.test(marker) ? `\\${marker}` : marker;
  return new RegExp(`${escapedMarker}(.*?)${escapedMarker}`, 'g');
}

/**
 * Returns the array of markers used for italic styles
 * @returns {Array} - Array of marker characters
 * @private
 */
function getItalicMarkers() {
  return [ASTERISK_MARKER, UNDERSCORE_MARKER];
}

/**
 * Process text recursively to handle all formatting cases, preserving bold segments.
 * This function identifies bold markdown segments and leaves them unmodified,
 * while processing the text before and after for italic formatting.
 * 
 * @example
 * // Returns: '**bold** <em>*italic*</em>'
 * processTextPreservingBold('**bold** *italic*');
 * 
 * @param {string} text - The text to process
 * @returns {string} - Processed text with HTML tags added around italics while preserving bold
 * @private
 */
function processTextPreservingBold(text) {
  // For recursive calls with empty segments, return early
  if (!text || text.trim() === '') {
    return '';
  }

  // First, identify any bold patterns
  const boldSegments = findBoldSegments(text);

  if (!boldSegments) {
    // No bold pattern found, process italics only
    return processAllItalicStyles(text);
  }

  // Extract the segments and immediately process them for the return value
  const { boldText, beforeText, afterText } = boldSegments;

  // Combine the processed sections with the unchanged bold text in a single return statement
  return (beforeText ? processAllItalicStyles(beforeText) : '') + 
         boldText + 
         (afterText ? processTextPreservingBold(afterText) : '');
}

// Main exported function

/**
 * Adds HTML <em> tags around text marked with Markdown italics while preserving
 * the original Markdown characters.
 * 
 * Handles both *single asterisk* and _underscore_ style Markdown italics.
 * Does NOT add <em> tags around bold markdown syntax (** or __).
 * 
 * @example
 * // Returns: '<em>*italic*</em> text'
 * italics('*italic* text');
 * 
 * @example
 * // Returns: '<em>_italic_</em> text'
 * italics('_italic_ text');
 * 
 * @example
 * // Returns: '**bold** and <em>*italic*</em>'
 * italics('**bold** and *italic*');
 * 
 * @param {string} text - The input text that may contain Markdown italics syntax
 * @returns {string} Text with HTML <em> tags added around Markdown-formatted italics
 */
export function italics(text) {
  if (!text || typeof text !== 'string') {
    return text;
  }

  // We'll use a recursive approach with special handling for bold segments
  return processTextPreservingBold(text);
}

// Helper functions for processing text

/**
 * Find bold segments in text and split into bold text and surrounding text
 * 
 * @param {string} text - The text to process
 * @returns {Object|null} - Object with boldText, beforeText, and afterText properties, or null if no bold found
 * @private
 */
function findBoldSegments(text) {
  const boldMatch = text.match(createBoldPattern());

  if (!boldMatch) {
    return null;
  }

  const boldText = boldMatch[0];
  const boldStartIndex = boldMatch.index;
  const boldEndIndex = boldStartIndex + boldText.length;

  return {
    boldText,
    beforeText: text.substring(0, boldStartIndex),
    afterText: text.substring(boldEndIndex)
  };
}

/**
 * Apply italic formatting for a specific marker to the text
 * @param {string} text - The text to process
 * @param {string} marker - The marker character (* or _)
 * @returns {string} - Text with the particular italic style formatted
 * @private
 */
function applyItalicFormatting(text, marker) {
  const pattern = createItalicsPattern(marker);
  return text.replace(pattern, (match, capturedContent) => {
    return createItalicReplacementString(capturedContent, marker);
  });
}

/**
 * Process text through all italic style types (asterisk and underscore)
 * 
 * @example
 * // Returns: '<em>*text*</em>'
 * processAllItalicStyles('*text*');
 * 
 * @example
 * // Returns: '<em>_text_</em>'
 * processAllItalicStyles('_text_');
 * 
 * @param {string} text - The text to process 
 * @returns {string} - Text with all italic styles formatted
 * @private
 */
function processAllItalicStyles(text) {
  // Process the text through all italic markers using reduce
  return getItalicMarkers().reduce(applyItalicFormatting, text);
}

/**
 * Wrap content with a marker at the beginning and end
 * @param {string} content - The content to wrap
 * @param {string} marker - The marker to add at beginning and end
 * @returns {string} - Content wrapped with markers
 * @private
 */
function wrapWithMarker(content, marker) {
  return `${marker}${content}${marker}`;
}

/**
 * Create an HTML tag wrapper for content
 * @param {string} tagName - The HTML tag name (without brackets)
 * @param {string} content - The content to wrap
 * @returns {string} - The content wrapped in the HTML tag
 * @private
 */
function wrapWithHtmlTag(tagName, content) {
  return [`<${tagName}>`, content, `</${tagName}>`].join('');
}

/**
 * Create a replacement string for italic markdown content
 * @param {string} content - The inner content of the markdown
 * @param {string} marker - The markdown marker character (* or _)
 * @returns {string} - HTML formatted replacement string
 * @private
 */
function createItalicReplacementString(content, marker) {
  // First wrap content with markdown markers, then with HTML tag
  return wrapWithHtmlTag('em', wrapWithMarker(content, marker));
}

// Audio controls functionality
(function() {
  const audioElements = document.querySelectorAll("audio");

  audioElements.forEach(function(audio, index) {
    audio.removeAttribute("controls");

    if (!audio.id) {
      audio.id = "audio-" + index;
    }

    const controlsContainer = document.createElement("div");
    controlsContainer.className = "audio-controls";
    controlsContainer.id = "controls-" + audio.id;

    const timeDisplay = document.createElement("span");
    timeDisplay.className = "audio-time";
    timeDisplay.textContent = "0:00";

    const playButton = document.createElement("a");
    playButton.href = "#";
    playButton.textContent = "PLAY";
    playButton.addEventListener("click", function(e) {
      e.preventDefault();
      audio.play();
    });

    const pauseButton = document.createElement("a");
    pauseButton.href = "#";
    pauseButton.textContent = "PAUSE";
    pauseButton.addEventListener("click", function(e) {
      e.preventDefault();
      audio.pause();
    });

    const stopButton = document.createElement("a");
    stopButton.href = "#";
    stopButton.textContent = "STOP";
    stopButton.addEventListener("click", function(e) {
      e.preventDefault();
      audio.pause();
      audio.currentTime = 0;
    });

    audio.addEventListener("timeupdate", function() {
      const minutes = Math.floor(audio.currentTime / 60);
      const seconds = Math.floor(audio.currentTime % 60).toString().padStart(2, "0");
      timeDisplay.textContent = minutes + ":" + seconds;
    });

    controlsContainer.appendChild(playButton);
    controlsContainer.appendChild(document.createTextNode(" "));
    controlsContainer.appendChild(pauseButton);
    controlsContainer.appendChild(document.createTextNode(" "));
    controlsContainer.appendChild(stopButton);
    controlsContainer.appendChild(document.createTextNode(" "));
    controlsContainer.appendChild(timeDisplay);

    audio.parentNode.insertBefore(controlsContainer, audio.nextSibling);
  });
})();

// Interactive components functionality
/**
 * Initialize an interactive component with a processing function
 * @param {string} id - The ID of the article element
 * @param {Function} processingFunction - The function to process input values
 */
function initializeInteractiveComponent(id, processingFunction) {
  // Get the article element
  const article = document.getElementById(id);

  // Get the elements within the article
  const inputElement = article.querySelector('input');
  const submitButton = article.querySelector('button');
  const outputElement = article.querySelector('p.output');

  // Disable controls during initialization
  inputElement.disabled = true;
  submitButton.disabled = true;

  // Update message to show JS is running
  outputElement.textContent = 'Initialising...';

  /**
   * Enable controls and update status message
   */
  function enableControls() {
    inputElement.disabled = false;
    submitButton.disabled = false;
    outputElement.textContent = 'Ready for input';
    outputElement.parentElement.classList.remove('warning');
  }

  /**
   * Handle form submission events
   * @param {Event} event - The submission event
   */
  function handleSubmit(event) {
    if (event) {
      event.preventDefault();
    }
    const inputValue = inputElement.value;

    // Use the provided processing function
    const result = processingFunction(inputValue);

    // Update the output
    outputElement.textContent = result;
  }

  // Add event listener to the submit button
  submitButton.addEventListener('click', handleSubmit);

  // Add event listener for Enter key in the input field
  inputElement.addEventListener('keypress', (event) => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  });

  // Enable controls when initialization is complete
  enableControls();
}

/**
 * Initialize a component when it enters the viewport
 * @param {string} id - The ID of the article element to observe
 * @param {string} modulePath - Path to the module containing the processing function
 * @param {string} functionName - Name of the function to import from the module
 */
function initializeWhenVisible(id, modulePath, functionName) {
  const article = document.getElementById(id);

  // Create an observer instance
  const observer = new IntersectionObserver((entries, observer) => {
    entries.forEach(entry => {
      // If the article is visible
      if (entry.isIntersecting) {
        // Dynamically import the module only when the article is visible
        import(modulePath).then((module) => {
          const processingFunction = module[functionName];

          // Initialize the component with the imported function
          initializeInteractiveComponent(id, processingFunction);
        }).catch(error => {
          console.error('Error loading module ' + modulePath + ':', error);
        });

        // Stop observing once initialized
        observer.disconnect();
      }
    });
  }, {
    // Options for the observer
    root: null, // viewport
    threshold: 0.1 // 10% visibility is enough to trigger
  });

  // Start observing the article
  observer.observe(article);
}

// Initialize all registered components when they become visible
if (window.interactiveComponents && window.interactiveComponents.length > 0) {
  console.log('Initializing', window.interactiveComponents.length, 'interactive components');
  window.interactiveComponents.forEach(component => {
    initializeWhenVisible(component.id, component.modulePath, component.functionName);
  });
} else {
  console.warn('No interactive components found to initialize');
}

// Tag filtering functionality
function hideArticlesByClass(className) {
  var articles = document.getElementsByTagName('article');
  for (var i = 0; i < articles.length; i++) {
    if (articles[i].classList.contains(className)) {
      articles[i].style.display = 'none';
    }
  }
}

function toggleHideLink(link, className) {
  // Check if a span with the hide link already exists immediately after the link.
  if (link.nextElementSibling && link.nextElementSibling.classList.contains('hide-span')) {
    // Remove the span if it exists.
    link.nextElementSibling.remove();
  } else {
    // Create a new span element.
    var span = document.createElement('span');
    span.classList.add('hide-span');
    // Append the opening text node.
    span.appendChild(document.createTextNode(" ("));

    // Create the hide anchor element.
    var hideLink = document.createElement('a');
    hideLink.textContent = "hide";
    // Add click listener to trigger hideArticlesByClass.
    hideLink.addEventListener('click', function(event) {
      event.preventDefault();
      hideArticlesByClass(className);
    });
    span.appendChild(hideLink);
    // Append the closing text node.
    span.appendChild(document.createTextNode(")"));

    // Insert the span immediately after the link.
    link.parentNode.insertBefore(span, link.nextSibling);
  }
}

(function() {
  Array.from(document.getElementsByTagName('a')).forEach(function(link) {
    Array.from(link.classList).forEach(function(className) {
      if (className.indexOf('tag-') === 0) {
        link.addEventListener('click', function(event) {
          event.preventDefault();
          toggleHideLink(link, className);
        });
        return; // exit after first tag- match
      }
    });
  });
})();

