// Single global state object
let globalState = {
  blog: null, // Holds the fetched blog data
  blogStatus: 'idle', // 'idle', 'loading', 'loaded', 'error'
  blogError: null, // Stores any error during fetch
  blogFetchPromise: null, // Tracks the ongoing fetch promise
  temporary: {} // Holds data managed by toys like setTemporary
};

import { setupAudio } from './audio-controls.js';
import { enableInteractiveControls } from './toy-controls.js';

// Helper Functions (moved to top level for broader scope)
const getElementById = (doc, id) => doc.getElementById(id);
const getAudioElements = (doc) => querySelectorAll(doc, "audio"); // Use querySelectorAll helper
const querySelector = (el, selector) => el.querySelector(selector);
const querySelectorAll = (docOrEl, selector) => docOrEl.querySelectorAll(selector);
const removeControlsAttribute = (audio) => audio.removeAttribute("controls");
const createElement = (doc, tag) => doc.createElement(tag);
const createTextNode = (doc) => doc.createTextNode(" ");
const stopDefault = (e) => e.preventDefault();
const playAudio = (audio) => audio.play();
const pauseAudio = (audio) => audio.pause();
const addEventListener = (element, event, func) => element.addEventListener(event, func);
const appendChild = (parentNode, newChild) => parentNode.appendChild(newChild);
const insertBefore = (parentNode, newChild, refChild) => parentNode.insertBefore(newChild, refChild);
const log = (...args) => console.log(...args);
const warn = (...args) => console.warn(...args);
const error = (...args) => console.error(...args);

// Interactive components functionality

/**
 * Initialize an interactive component with a processing function
 * @param {string} id - The ID of the article element
 * @param {Function} processingFunction - The function to process input values
 */
function initializeInteractiveComponent(id, processingFunction) {
  // Get the article element
  const article = getElementById(document, id);

  // Get the elements within the article
  const inputElement = querySelector(article, 'input');
  const submitButton = querySelector(article, 'button');
  const outputElement = querySelector(article, 'p.output');

  // Disable controls during initialization
  inputElement.disabled = true;
  submitButton.disabled = true;

  // Update message to show JS is running
  outputElement.textContent = 'Initialising...';

  /**
   * Handle form submission events
   * @param {Event} event - The submission event
   */
  const createHandleSubmit = (inputElement, globalState) => (event) => {
    if (event) {
      stopDefault(event);
    }
    const inputValue = inputElement.value;

    try {
      // Create an env Map with utility functions that might be needed by processing functions
      const env = new Map([
        ["getRandomNumber", () => Math.random()],
        ["getCurrentTime", () => new Date().toISOString()],
        ["getData", () => {
          // Return a deep copy of the current global state
          // Using JSON parse/stringify for a simple deep copy
          const stateCopy = JSON.parse(JSON.stringify(globalState));

          // Check blog status and trigger fetch if needed, but don't block
          if (stateCopy.blogStatus === 'idle') {
            fetchAndCacheBlogData(); // Trigger fetch (no await)
          } else if (stateCopy.blogStatus === 'error') {
            warn("Blog data previously failed to load:", stateCopy.blogError);
          }

          // Remove fetch-related properties from the copy returned to the toy
          delete stateCopy.blogStatus;
          delete stateCopy.blogError;
          delete stateCopy.blogFetchPromise;

          return stateCopy; 
        }],
        ["setData", (newData) => {
          // Replace the entire global state, but validate basic structure
          if (typeof newData === 'object' && newData !== null && newData.hasOwnProperty('temporary')) {
            // Preserve the internal blog loading state properties when updating
            const currentBlogStatus = globalState.blogStatus;
            const currentBlogError = globalState.blogError;
            const currentBlogFetchPromise = globalState.blogFetchPromise;
            const currentBlogData = globalState.blog; // Preserve actual blog data too

            globalState = newData;

            // Restore internal properties
            globalState.blogStatus = currentBlogStatus;
            globalState.blogError = currentBlogError;
            globalState.blogFetchPromise = currentBlogFetchPromise;
            // Ensure the blog data wasn't wiped out if it wasn't included in newData
            if (!newData.hasOwnProperty('blog')) {
              globalState.blog = currentBlogData;
            }

            log('Global state updated:', globalState);
          } else {
            error('setData received invalid data structure:', newData);
            throw new Error('setData requires an object with at least a \'temporary\' property.');
          }
        }]
      ]);

      // Call the processing function with the input value
      // If the function accepts two parameters (length === 2), it will receive the env Map
      // If it only accepts one parameter, the second argument (env) will be ignored by the function
      const result = processingFunction.length === 2
                 ? processingFunction(inputValue, env)
                 : processingFunction(inputValue);

      // Update the output
      outputElement.textContent = result;
    } catch (error) {
      error('Error processing input:', error);
      outputElement.textContent = 'Error: ' + error.message;
      outputElement.parentElement.classList.add('warning');
    }
  };
  const handleSubmit = createHandleSubmit(inputElement, globalState);

  // Add event listener to the submit button
  addEventListener(submitButton, 'click', handleSubmit);

  // Add event listener for Enter key in the input field
  addEventListener(inputElement, 'keypress', (event) => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  });

  // Enable controls when initialization is complete
  enableInteractiveControls(inputElement, submitButton, outputElement);
}

/**
 * Initialize a component when it enters the viewport
 * @param {string} id - The ID of the article element to observe
 * @param {string} modulePath - Path to the module containing the processing function
 * @param {string} functionName - Name of the function to import from the module
 */
function initializeWhenVisible(id, modulePath, functionName) {
  const article = getElementById(document, id);

  // Create an observer instance
  const observer = new IntersectionObserver((entries, observer) => {
    entries.forEach(entry => {
      // If the article is visible
      if (entry.isIntersecting) {
        // Dynamically import the module only when the article is visible
        import(modulePath).then((module) => {
          const processingFunction = module[functionName];

          // Initialize the component with the imported function
          initializeInteractiveComponent(id, processingFunction);
        }).catch(error => {
          error('Error loading module ' + modulePath + ':', error);
        });

        // Stop observing once initialized
        observer.disconnect();
      }
    });
  }, {
    // Options for the observer
    root: null, // viewport
    threshold: 0.1 // 10% visibility is enough to trigger
  });

  // Start observing the article
  observer.observe(article);
}

// Initialize all registered components when they become visible
if (window.interactiveComponents && window.interactiveComponents.length > 0) {
  log('Initializing', window.interactiveComponents.length, 'interactive components');
  window.interactiveComponents.forEach(component => {
    initializeWhenVisible(component.id, component.modulePath, component.functionName);
  });
} else {
  warn('No interactive components found to initialize');
}

// Tag filtering functionality
function hideArticlesByClass(className) {
  var articles = document.getElementsByTagName('article');
  for (var i = 0; i < articles.length; i++) {
    if (articles[i].classList.contains(className)) {
      articles[i].style.display = 'none';
    }
  }
}

function toggleHideLink(link, className) {
  // Check if a span with the hide link already exists immediately after the link.
  if (link.nextElementSibling && link.nextElementSibling.classList.contains('hide-span')) {
    // Remove the span if it exists.
    link.nextElementSibling.remove();
  } else {
    // Create a new span element.
    var span = createElement(document, 'span');
    span.classList.add('hide-span');
    // Append the opening text node.
    appendChild(span, document.createTextNode(" ("));

    // Create the hide anchor element.
    var hideLink = createElement(document, 'a');
    hideLink.textContent = "hide";
    // Add click listener to trigger hideArticlesByClass.
    addEventListener(hideLink, 'click', function(event) {
      stopDefault(event);
      hideArticlesByClass(className);
    });
    appendChild(span, hideLink);
    // Append the closing text node.
    appendChild(span, document.createTextNode(")"));

    // Insert the span immediately after the link.
    insertBefore(link.parentNode, span, link.nextSibling);
  }
}

const handleTagLinks = () => {
  Array.from(document.getElementsByTagName('a')).forEach(link => {
    Array.from(link.classList).forEach(className => {
      if (className.indexOf('tag-') === 0) {
        addEventListener(link, 'click', event => {
          stopDefault(event);
          toggleHideLink(link, className);
        });
        return; // exit after first tag- match
      }
    });
  });
};

handleTagLinks();

/**
 * Fetches blog data and updates the global state.
 * Ensures only one fetch happens at a time.
 */
function fetchAndCacheBlogData() {
  // Prevent multiple simultaneous fetches
  if (globalState.blogStatus === 'loading' && globalState.blogFetchPromise) {
    log('Blog data fetch already in progress.');
    return globalState.blogFetchPromise; 
  }

  log('Starting to fetch blog data...');
  globalState.blogStatus = 'loading';
  globalState.blogError = null;

  globalState.blogFetchPromise = fetch('./blog.json') 
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      globalState.blog = data; // Update the blog property
      globalState.blogStatus = 'loaded';
      log('Blog data loaded successfully into globalState.');
      globalState.blogFetchPromise = null; // Clear promise on success
    })
    .catch(error => {
      error('Error loading blog data:', error);
      globalState.blogError = error;
      globalState.blogStatus = 'error';
      globalState.blogFetchPromise = null; // Clear promise on error
    });

  return globalState.blogFetchPromise;
}

// Initial fetch of blog data when the script loads
fetchAndCacheBlogData();

setupAudio(
  document,
  getAudioElements,
  removeControlsAttribute,
  createElement,
  createTextNode,
  stopDefault,
  playAudio,
  pauseAudio,
  addEventListener,
  appendChild,
  insertBefore
);

#!/usr/bin/env node

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// Get __dirname equivalent in ES module scope
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Define base directories
const projectRoot = path.resolve(__dirname, '../..'); // Adjust based on script location
const srcDir = path.resolve(projectRoot, 'src');
const publicDir = path.resolve(projectRoot, 'public');
const srcToysDir = path.resolve(srcDir, 'toys');
const srcBrowserDir = path.resolve(srcDir, 'browser');

// Ensure public directory exists
if (!fs.existsSync(publicDir)) {
  fs.mkdirSync(publicDir, { recursive: true });
}

// --- Copy Toy Files --- 

// Function to recursively find JS files in the toys directory
function findJsFiles(dir) {
  let jsFiles = [];
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);
    if (entry.isDirectory()) {
      jsFiles = jsFiles.concat(findJsFiles(fullPath));
    } else if (entry.isFile() && entry.name.endsWith('.js') && !entry.name.endsWith('.test.js')) {
      jsFiles.push(fullPath);
    }
  }
  return jsFiles;
}

// Find all JS files in src/toys
const toyFiles = findJsFiles(srcToysDir);

// Copy each toy file to the corresponding path in public
toyFiles.forEach(filePath => {
  const relativePath = path.relative(srcToysDir, filePath);
  const destPath = path.join(publicDir, relativePath);
  const destDir = path.dirname(destPath);

  // Ensure the destination directory exists
  if (!fs.existsSync(destDir)) {
    fs.mkdirSync(destDir, { recursive: true });
  }

  // Copy the file
  fs.copyFileSync(filePath, destPath);
  console.log(`Copied: ${filePath} -> ${destPath}`);
});

console.log('Toy files copied successfully!');

// --- Copy Specific Assets --- 

const assetsToCopy = [
  { src: path.join(srcDir, 'blog.json'), dest: path.join(publicDir, 'blog.json') },
  { src: path.join(srcBrowserDir, 'main.js'), dest: path.join(publicDir, 'main.js') },
  { src: path.join(srcBrowserDir, 'toy-controls.js'), dest: path.join(publicDir, 'toy-controls.js') },
  { src: path.join(srcBrowserDir, 'audio-controls.js'), dest: path.join(publicDir, 'audio-controls.js') }
];

assetsToCopy.forEach(asset => {
  if (fs.existsSync(asset.src)) {
    fs.copyFileSync(asset.src, asset.dest);
    console.log(`Copied: ${asset.src} -> ${asset.dest}`);
  } else {
    console.warn(`Warning: Asset not found, skipping copy: ${asset.src}`);
  }
});

console.log('Specific assets copied successfully!');

function getZeroVariantResult(num) {
  const result = isZeroVariant(num);
  return result !== null ? result : null;
}


function getIEEEDecomposition(num) {
  const parts = decomposeIEEE754(num);
  if (!isValidIEEEParts(parts)) return null;
  return getSignificandAndExponent(parts);
}

function formatFloatDecomposition(decimal, { B, C }) {
  return `${decimal} (${B.toString()} × 2^${C.toString()})`;
}

function getZeroVariantString(num) {
  return getZeroVariantResult(num);
}

function resolveZeroVariant(num) {
  const zeroResult = getZeroVariantString(num);
  return zeroResult ? zeroResult : null;
}

export function decomposeFloat(input) {
  const num = Number(input);
  if (!Number.isFinite(num)) return "";

  const zeroReturn = resolveZeroVariant(num);
  if (zeroReturn) return zeroReturn;

  const A = formatDecimal(num);
  const decomposition = getIEEEDecomposition(num);
  if (!decomposition) return "";

  return formatFloatDecomposition(A, decomposition);
}

function isPositiveZero(n) {
  return Object.is(n, 0);
}

function isNegativeZero(n) {
  return Object.is(n, -0);
}

function isPositiveZeroResult(num) {
  return isPositiveZero(num) ? "0 (0 × 2^0)" : null;
}

function isNegativeZeroResult(num) {
  return isNegativeZero(num) ? "0 (-0 × 2^0)" : null;
}

function isZeroVariant(num) {
  return isPositiveZeroResult(num) || isNegativeZeroResult(num);
}

function formatDecimal(num) {
  let A = num.toPrecision(17);
  return A.includes('.') ? A.replace(/\.?0+$/, '') : A;
}

function hasIEEEFields(parts) {
  return ['sign', 'mantissa', 'exponent'].every(key => key in parts);
}

function isValidIEEEParts(parts) {
  return Boolean(parts) && hasIEEEFields(parts);
}

function getSignificandAndExponent({ sign, mantissa, exponent }) {
  const signValue = sign === 0 ? 1n : -1n;
  const fullSignificand = (1n << 52n) | BigInt(mantissa);
  const B = signValue * fullSignificand;
  const C = BigInt(exponent - 1023) - 52n;
  return { B, C };
}

function decomposeIEEE754(value) {
  if (!Number.isFinite(value)) return {};

  const bits = getFloat64Bits(value);
  return extractIEEEComponents(bits);
}

function getFloat64Bits(value) {
  const buffer = new ArrayBuffer(8);
  const floatView = new Float64Array(buffer);
  const byteView = new Uint8Array(buffer);

  floatView[0] = value;

  let bits = 0n;
  for (let i = 7; i >= 0; i--) {
    bits = (bits << 8n) | BigInt(byteView[i]);
  }

  return bits;
}

function extractIEEEComponents(bits) {
  const sign = Number((bits >> 63n) & 1n);
  const exponentBits = (bits >> 52n) & 0x7FFn;
  const mantissaBits = bits & 0xFFFFFFFFFFFFFn;

  if (exponentBits === 0n) {
    return {};
  }

  return {
    sign,
    mantissa: Number(mantissaBits),
    exponent: Number(exponentBits),
  };
}

function isRecognizedBait(baitKey, baitOptions) {
  return baitKey in baitOptions;
}

function isEmptyBait(baitKey) {
  return baitKey.length === 0;
}

function getDefaultBaitResponse(moodDescription) {
  return {
    isError: true,
    message: `You cast your line with nothing but hesitation. Without any bait, the waters remain undisturbed in their ${moodDescription}.`
  };
}

function getRecognizedBait(baitKey, baitOptions) {
  return baitOptions[baitKey];
}

function getUnrecognizedBait() {
  return { modifier: 0, description: "an unconventional bait" };
}

function getBaitData(input, baitOptions, moodDescription) {
  const baitKey = input.trim().toLowerCase();
  if (isRecognizedBait(baitKey, baitOptions)) {
    return getRecognizedBait(baitKey, baitOptions);
  }
  if (isEmptyBait(baitKey)) {
    return getDefaultBaitResponse(moodDescription);
  }
  return getUnrecognizedBait();
}

function getTimeOfDay(hour) {
  const ranges = [
    { start: 5, end: 12, label: "morning" },
    { start: 12, end: 17, label: "afternoon" },
    { start: 17, end: 21, label: "evening" },
    { start: 21, end: 24, label: "night" },
    { start: 0, end: 5, label: "night" },
  ];
  const match = ranges.find(({ start, end }) => hour >= start && hour < end);
  if (!match) throw new Error(`Unrecognized hour: ${hour}`);
  return match.label;
}

function getSeason(month) {
  const ranges = [
    { months: [11, 0, 1], label: "winter" },
    { months: [2, 3, 4], label: "spring" },
    { months: [5, 6, 7], label: "summer" },
    { months: [8, 9, 10], label: "fall" },
  ];
  const match = ranges.find(({ months }) => months.includes(month));
  if (!match) throw new Error(`Unrecognized month: ${month}`);
  return match.label;
}

function getMoodDescription(season, timeOfDay) {
  const seasonDescriptions = {
    winter: "crisp, icy waters",
    spring: "bubbling, fresh currents",
    summer: "warm, shimmering waves",
    fall: "cool, reflective ponds",
  };
  const timeDescriptions = {
    morning: "as dawn breaks with promise",
    afternoon: "under a vibrant sun",
    evening: "in the glow of twilight",
    night: "beneath a silent, starry sky",
  };
  return `${seasonDescriptions[season]} ${timeDescriptions[timeOfDay]}`;
}

function isSilentCatch(chance) {
  return chance < 0.3;
}

function isCommonCatch(chance) {
  return chance < 0.6;
}

function isTroutCatch(chance) {
  return chance < 0.85;
}

const fishingOutcomes = [
  {
    check: isSilentCatch,
    describe: (bait, mood) =>
      `the water stays silent. Despite your use of ${bait}, no fish disturb the ${mood}.`,
  },
  {
    check: isCommonCatch,
    describe: (bait, mood) =>
      `a common carp surfaces gently, a modest reward for your effort with ${bait}, set against ${mood}.`,
  },
  {
    check: isTroutCatch,
    describe: (bait, mood) =>
      `a glimmering trout appears briefly, its shimmer echoing the beauty of ${mood}. Your choice of ${bait} worked well.`,
  },
  {
    check: () => true,
    describe: (bait, mood) =>
      `in a burst of brilliance, a legendary golden fish leaps forth—its radiance matching the splendor of ${mood}. Your ${bait} has yielded a prize.`,
  },
];

function getFishingOutcome(effectiveChance, baitDescription, moodDescription) {
  return fishingOutcomes.find(({ check }) => check(effectiveChance)).describe(baitDescription, moodDescription);
}

function getBaitOptions() {
  return {
    "worm": { modifier: 0.0, description: "a wriggling worm" },
    "insect": { modifier: 0.05, description: "a lively insect" },
    "bread": { modifier: -0.05, description: "a slice of bread" },
    "cheese": { modifier: 0.1, description: "a pungent piece of cheese" },
    "shiny bait": { modifier: 0.15, description: "a glittering lure" },
    "doughnut": { modifier: 0.2, description: "a tempting doughnut" },
    "grub": { modifier: 0.05, description: "a succulent grub" },
    "minnow": { modifier: 0.1, description: "a darting minnow" },
    "sausage": { modifier: 0.2, description: "a savory sausage" },
    "maggot": { modifier: -0.1, description: "a squirming maggot" },
  };
}

function getTimeContext(getCurrentTime) {
  const date = new Date(getCurrentTime());
  const month = date.getMonth();
  const hour = date.getHours();
  const season = getSeason(month);
  const timeOfDay = getTimeOfDay(hour);
  return { season, timeOfDay };
}

function fishingGame(input, env) {
  const { season, timeOfDay } = getTimeContext(env.get("getCurrentTime"));

  const moodDescription = getMoodDescription(season, timeOfDay);

  const baitOptions = getBaitOptions();

  const baitDataOrError = getBaitData(input, baitOptions, moodDescription);
  if (baitDataOrError.isError) return baitDataOrError.message;
  const baitData = baitDataOrError;

  const getRandomNumber = env.get("getRandomNumber");
  const baseChance = getRandomNumber();
  const effectiveChance = Math.min(1, Math.max(0, baseChance + baitData.modifier));

  const outcome = getFishingOutcome(effectiveChance, baitData.description, moodDescription);

  return `Casting your line with ${baitData.description}, you await a catch. ${outcome}`;
}

export { fishingGame };

/**
 * Retrieves a value from data provided by the environment using the input as a path.
 * @param {string} input - The path (e.g., 'key1.key2.0.key3') to look up in the data.
 * @param {Map<string, Function>} env - Environment map containing dependencies. Expected: 'getData'.
 * @returns {string} The JSON stringified value found at the path, or an error message.
 */
export function get(input, env) {
  if (!env || typeof env.get !== 'function') {
    return "Error: 'env' Map with 'get' method is required.";
  }
  const getData = env.get('getData');
  if (typeof getData !== 'function') {
    return "Error: 'getData' function not found in env.";
  }

  try {
    const data = getData();
    if (input.trim() === '') {
      return JSON.stringify(data);
    }
    // Basic check if initial data is an object or array
    if (data === null || (typeof data !== 'object' && !Array.isArray(data))) {
        return "Error: 'getData' did not return a valid object or array.";
    }

    // Split the input path by dots
    const pathSegments = input.split('.');
    let currentValue = data;
    let currentPath = '';

    // Traverse the path
    for (const segment of pathSegments) {
      currentPath = currentPath ? `${currentPath}.${segment}` : segment;
      if (currentValue === null || typeof currentValue !== 'object') {
        return `Error: Cannot access property '${segment}' on non-object value at path '${currentPath.substring(0, currentPath.lastIndexOf('.') !== -1 ? currentPath.lastIndexOf('.') : 0)}'. Value is: ${JSON.stringify(currentValue)}`;
      }

      if (Object.prototype.hasOwnProperty.call(currentValue, segment)) {
        currentValue = currentValue[segment];
      } else {
        return `Error: Path segment '${segment}' not found at '${currentPath}'. Available keys/indices: ${Object.keys(currentValue).join(', ')}`;
      }
    }

    // Return a string representation of the final value
    try {
      return JSON.stringify(currentValue);
    } catch (stringifyError) {
        return `Error stringifying final value at path "${input}": ${stringifyError.message}`;
    }
  } catch (error) {
      // Catch errors from getData() execution or other unexpected issues
      return `Error during data retrieval or path traversal for "${input}": ${error.message}`;
  }
}

/**
 * Checks if an item is a plain object (not an array or null).
 * @param {*} item - The item to check.
 * @returns {boolean}
 */
function isObject(item) {
  return (item && typeof item === 'object' && !Array.isArray(item));
}

/**
 * Deeply merges two objects. Creates a new object with merged properties.
 * Properties in source will overwrite properties in target, unless both
 * properties are plain objects, in which case they are recursively merged.
 * Arrays and other types are overwritten, not merged.
 * @param {object} target - The target object.
 * @param {object} source - The source object.
 * @returns {object} A new object representing the merged result.
 */
function deepMerge(target, source) {
  const output = { ...target }; // Start with a shallow copy of the target
  if (isObject(target) && isObject(source)) {
    Object.keys(source).forEach(key => {
      const targetValue = target[key];
      const sourceValue = source[key];
      if (isObject(targetValue) && isObject(sourceValue)) {
        // If both target and source values are objects, recursively merge
        output[key] = deepMerge(targetValue, sourceValue);
      } else {
        // Otherwise, overwrite with the source value
        // (If sourceValue is an object, this assignment handles the case
        // where target[key] wasn't an object or didn't exist)
        output[key] = sourceValue;
      }
    });
  } else if (isObject(source)) {
      // If target is not an object but source is, return a shallow copy of source
      // (or deep copy if required, but shallow should suffice here as we merge onto it)
      return { ...source };
  }
  // If source is not an object, the initial shallow copy of target is returned
  // or target itself if it wasn't an object either (though initial checks prevent this)
  return output;
}

/**
 * Parses input as JSON, deep merges it into the 'temporary' object obtained via env.getData(),
 * and then passes the entire modified data structure back to env.setData().
 * @param {string} input - A JSON string to parse and merge.
 * @param {Map<string, Function>} env - Environment map. Expected: 'getData()', 'setData(data)'.
 * @returns {string} A confirmation message or an error message.
 */
export function setTemporary(input, env) {
  let inputJson;
  try {
    inputJson = JSON.parse(input);
  } catch (parseError) {
    return `Error: Invalid JSON input. ${parseError.message}`;
  }

  if (!isObject(inputJson)) { // Use the helper
      return "Error: Input JSON must be a plain object.";
  }

  if (!env || typeof env.get !== 'function') {
    return "Error: 'env' Map with 'get' method is required.";
  }
  const getData = env.get('getData');
  const setData = env.get('setData');
  if (typeof getData !== 'function') {
    return "Error: 'getData' function not found in env.";
  }
  if (typeof setData !== 'function') {
      return "Error: 'setData' function not found in env.";
  }

  try {
    const currentData = getData(); 

    if (!isObject(currentData)) { // Use the helper
        return "Error: 'getData' did not return a valid object.";
    }

    // Deep clone currentData to create newData - JSON method is simple but has limitations (e.g., with Dates, Functions)
    // For this use case, it should be acceptable.
    const newData = JSON.parse(JSON.stringify(currentData));

    // Ensure the 'temporary' key exists and is an object in the new copy
    if (!isObject(newData.temporary)) {
        newData.temporary = {}; 
    }

    // Perform the deep merge
    newData.temporary = deepMerge(newData.temporary, inputJson);

    setData(newData);

    return `Success: Temporary data deep merged.`; 

  } catch (error) {
    return `Error updating temporary data: ${error.message}`;
  }
}

export function cyberpunkAdventure(input, env) {
  try {
    const getRandomNumber = env.get("getRandomNumber");
    const getCurrentTime = env.get("getCurrentTime");
    const getData = env.get("getData");
    const setTemporaryData = env.get("setData");
    const { temporary } = getData();
    const scoped = temporary.CYBE1 || {};

    const name = scoped.name || input.trim() || "Stray";
    const state = scoped.state || "intro";
    const inventory = scoped.inventory || [];
    const visited = new Set(scoped.visited || []);

    const lowerInput = input.trim().toLowerCase();
    const time = getCurrentTime();

    let output = "";
    let nextState = state;
    let nextInventory = [...inventory];
    let nextVisited = new Set(visited);

    // If we haven’t stored the name yet, do so now
    if (!scoped.name) {
      setTemporaryData({ temporary: { CYBE1: { name } } });
      return `> Welcome, ${name}. Your story begins now.\n> Type 'start' to continue.`;
    }

    // Game logic
    switch (state) {
      case "intro":
        output = `> ${time}\n> ${name}, you're in the Neon Market. Lights hum. Faces blur.\n> You see paths to: Hacker Den, Transport Hub, and Back Alley.\n> Where do you go? (hacker / transport / alley)`;
        nextState = "hub";
        break;

      case "hub":
        if (lowerInput.includes("hacker")) {
          output = `> You approach the Hacker Den. The door requires a password.`;
          nextState = "hacker:door";
        } else if (lowerInput.includes("transport")) {
          output = `> You head to the Transport Hub. Trains screech overhead.`;
          nextState = "transport:platform";
        } else if (lowerInput.includes("alley")) {
          output = `> You slip into the Back Alley. The shadows move with you.`;
          nextState = "alley:stealth";
        } else {
          output = `> Unclear direction. Options: hacker / transport / alley`;
        }
        break;

      // Hacker Den phases
      case "hacker:door":
        if (lowerInput.includes("zero")) {
          output = `> Password accepted. Inside, a rogue AI offers you a cracked implant.`;
          nextInventory.push("cracked implant");
          nextVisited.add("hacker");
          nextState = "hub";
        } else {
          output = `> Hint: the password is a number and a name...`;
        }
        break;

      // Transport Hub phases
      case "transport:platform":
        output = `> A vendor offers you a neural ticket in exchange for your datapad.`;
        nextState = "transport:trade";
        break;

      case "transport:trade":
        if (nextInventory.includes("datapad") && lowerInput.includes("trade")) {
          output = `> You hand over the datapad. The vendor grins and slips you the neural ticket.`;
          nextInventory.splice(nextInventory.indexOf("datapad"), 1);
          nextInventory.push("neural ticket");
          nextVisited.add("transport");
          nextState = "hub";
        } else {
          output = `> Do you want to trade? Type 'trade datapad'.`;
        }
        break;

      // Back Alley phases
      case "alley:stealth":
        const stealthCheck = getRandomNumber();
        const success = stealthCheck > 0.3;
        if (success) {
          output = `> You dodge the shadows and find a hidden stash: a stimpack.`;
          nextInventory.push("stimpack");
          nextVisited.add("alley");
        } else {
          output = `> You trip a wire. Sirens start up. You sprint back to the Market.`;
        }
        nextState = "hub";
        break;

      default:
        output = `> Glitch in the grid. Resetting...`;
        nextState = "intro";
    }

    setTemporaryData({
      temporary: {
        CYBE1: {
          name,
          state: nextState,
          inventory: nextInventory,
          visited: [...nextVisited]
        }
      }
    });

    return output;
  } catch {
    return `> SYSTEM ERROR: neural link failure`;
  }
}

