const INTERNAL_STATE_KEYS = ['blogStatus', 'blogError', 'blogFetchPromise'];

const BLOG_STATUS = {
  IDLE: 'idle',
  LOADING: 'loading',
  LOADED: 'loaded',
  ERROR: 'error'
};

function getBlogState(globalState) {
  return {
    status: globalState.blogStatus,
    error: globalState.blogError,
    fetchPromise: globalState.blogFetchPromise,
    data: globalState.blog,
  };
}

function isFetchInProgress(globalState) {
  const { status, fetchPromise } = getBlogState(globalState);
  return status === BLOG_STATUS.LOADING && fetchPromise;
}

export function shouldUseExistingFetch(globalState, logFn) {
  if (isFetchInProgress(globalState)) {
    logFn('Blog data fetch already in progress.');
    return true;
  }
  return false;
} 

/**
 * Fetches blog data and updates the global state.
 * Ensures only one fetch happens at a time.
 * @param {object} globalState - The global state object.
 * @param {function} fetchFn - The fetch function to use.
 * @param {function} logFn - The logging function to use.
 * @param {function} errorFn - The error logging function to use.
 */
export function fetchAndCacheBlogData(globalState, fetchFn, logFn, errorFn) {
  // Prevent multiple simultaneous fetches
  if (isFetchInProgress(globalState)) {
    logFn('Blog data fetch already in progress.');
    return globalState.blogFetchPromise; 
  }

  logFn('Starting to fetch blog data...');
  globalState.blogStatus = BLOG_STATUS.LOADING;
  globalState.blogError = null;

  globalState.blogFetchPromise = fetchFn('./blog.json') 
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      globalState.blog = data; // Update the blog property
      globalState.blogStatus = BLOG_STATUS.LOADED;
      logFn('Blog data loaded successfully:', data);
    })
    .catch(err => {
      globalState.blogStatus = BLOG_STATUS.ERROR;
      globalState.blogError = err;
      errorFn('Error fetching blog data:', err);
    })
    .finally(() => {
      globalState.blogFetchPromise = null; // Clear the promise tracking
    });

  return globalState.blogFetchPromise; // Return the promise for potential chaining
}

// Helper function needed by getData
export const getDeepStateCopy = (globalState) => JSON.parse(JSON.stringify(globalState));

function stripInternalFields(stateCopy) {
  for (const key of INTERNAL_STATE_KEYS) {
    delete stateCopy[key];
  }
}

function restoreBlogState(globalState, blogState) {
  globalState.blogStatus = blogState.status;
  globalState.blogError = blogState.error;
  globalState.blogFetchPromise = blogState.fetchPromise;
  globalState.blog = blogState.data;
}

function shouldCopyStateForFetch(status) {
  return status === BLOG_STATUS.IDLE || status === BLOG_STATUS.ERROR;
}

function isInvalidState(value) {
  return !(value && typeof value === 'object' && value.hasOwnProperty('temporary'));
}

function validateIncomingState(incomingState, errorFn) {
  if (isInvalidState(incomingState)) {
    errorFn('setData received invalid data structure:', incomingState);
    throw new Error("setData requires an object with at least a 'temporary' property.");
  }
}

function tryFetchingBlog(status, globalState, fetchFn, logFn, errorFn) {
  if (status === BLOG_STATUS.IDLE) {
    fetchAndCacheBlogData(globalState, fetchFn, logFn, errorFn);
  }
}

function maybeLogFetchError(status, error, warnFn) {
  if (status === BLOG_STATUS.ERROR) {
    warnFn("Blog data previously failed to load:", error);
  }
}

function handleBlogFetchState(status, error, globalState, fetchFn, logFn, errorFn, warnFn) {
  tryFetchingBlog(status, globalState, fetchFn, logFn, errorFn);
  maybeLogFetchError(status, error, warnFn);
}

/**
 * Gets a deep copy of the current global state, suitable for passing to toys.
 * It also handles initiating the blog data fetch if needed.
 * @param {object} globalState - The main application state.
 * @param {function} fetchFn - The fetch function.
 * @param {function} logFn - The logging function.
 * @param {function} errorFn - The error logging function.
 * @param {function} warnFn - The warning logging function.
 * @returns {object} A deep copy of the relevant state for the toy.
 */
export const getData = (globalState, fetchFn, logFn, errorFn, warnFn) => {
  const { status, error } = getBlogState(globalState);
  const stateCopy = shouldCopyStateForFetch(status) ? getDeepStateCopy(globalState) : globalState;

  handleBlogFetchState(status, error, globalState, fetchFn, logFn, errorFn, warnFn);

  stripInternalFields(stateCopy);
  return stateCopy;
};

/**
 * Updates the global state, preserving internal fetch/blog properties.
 * @param {object} incomingState - The new state object (must have 'temporary').
 * @param {object} globalState - The current global state to modify.
 * @param {function} logFn - The logging function.
 * @param {function} errorFn - The error logging function.
 */
export const setData = (incomingState, globalState, logFn, errorFn) => {
  // Replace the entire global state, but validate basic structure
  validateIncomingState(incomingState, errorFn);
  const oldBlogState = getBlogState(globalState);
  Object.assign(globalState, incomingState);
  restoreBlogState(globalState, oldBlogState);

  logFn('Global state updated:', globalState);
};

let globalState = {
  blog: null, // Holds the fetched blog data
  blogStatus: 'idle', // 'idle', 'loading', 'loaded', 'error'
  blogError: null, // Stores any error during fetch
  blogFetchPromise: null, // Tracks the ongoing fetch promise
  temporary: {} // Holds data managed by toys like setTemporary
};

import { setupAudio } from './audio-controls.js';
import { initializeVisibleComponents, handleModuleError, initialiseModule } from './toys.js';
import { hideArticlesByClass, toggleHideLink } from './tags.js';
import { fetchAndCacheBlogData, getData, setData } from './data.js';
import {
  getElementById,
  getAudioElements,
  querySelector,
  querySelectorAll,
  removeControlsAttribute,
  createElement,
  createTextNode,
  stopDefault,
  playAudio,
  pauseAudio,
  addEventListener,
  appendChild,
  insertBefore,
  log,
  warn,
  error,
  addWarning,
  getRandomNumber,
  getCurrentTime,
  hasNextSiblingClass,
  removeNextSibling,
  setTextContent
} from './document.js';

/**
 * Imports a module dynamically with success and error handling
 * @param {string} modulePath - Path to the module to import
 * @param {Function} onSuccess - Function to call when import succeeds
 * @param {Function} onError - Function to call when import fails
 */
function importModule(modulePath, onSuccess, onError) {
  import(modulePath).then(onSuccess).catch(onError);
}

const createHandleClick = (link, className) => event => {
  stopDefault(event);
  toggleHideLink(
    link,
    className,
    hasNextSiblingClass,
    removeNextSibling,
    createHideSpan
  );
};

const createHideSpan = (link, className) => {
  var span = createElement(document, 'span');
  span.classList.add('hide-span');
  appendChild(span, document.createTextNode(" ("));

  var hideLink = createElement(document, 'a');
  hideLink.textContent = "hide";
  addEventListener(hideLink, 'click', function(event) {
    stopDefault(event);
    hideArticlesByClass(
      className,
      tagName => document.getElementsByTagName(tagName),
      (element, cls) => element.classList.contains(cls),
      element => element.style.display = 'none'
    );
  });

  appendChild(span, hideLink);
  appendChild(span, document.createTextNode(")"));
  insertBefore(link.parentNode, span, link.nextSibling);
};

function createEnv() {
  return new Map([
    ["getRandomNumber", getRandomNumber],
    ["getCurrentTime", getCurrentTime],
    ["getData", () => getData(globalState, fetch, log, error, warn)],
    ["setData", (newData) => setData(newData, globalState, log, error)]
  ]);
}

function createIntersectionObserver(article, modulePath, functionName) {
  return new IntersectionObserver((entries, observer) =>
    handleIntersectionEntries(entries, observer, modulePath, article, functionName), {
    root: null,
    threshold: 0.1
  });
}

function handleIntersection(entry, observer, modulePath, article, functionName) {
  if (entry.isIntersecting) {
    const dom = { createElement, setTextContent, stopDefault, addWarning, addEventListener, querySelector };
    importModule(
      modulePath,
      initialiseModule(article, functionName, globalState, createEnv, error, fetch, dom),
      handleModuleError(modulePath, error)
    );
    observer.disconnect();
  }
}

function handleIntersectionEntries(entries, observer, modulePath, article, functionName) {
  entries.forEach(entry => handleIntersection(entry, observer, modulePath, article, functionName));
}

// Interactive components functionality
initializeVisibleComponents(
  window, 
  document, 
  log, 
  warn, 
  getElementById, 
  createIntersectionObserver // Pass the function defined in main.js
);

// Tag filtering functionality

const handleTagLinks = () => {
  const handleLink = link => {
    const handleClassName = className => {
      if (className.indexOf('tag-') === 0) {
        const handleClick = createHandleClick(link, className);
        addEventListener(link, 'click', handleClick);
        return; // exit after first tag- match
      }
    };

    Array.from(link.classList).forEach(handleClassName);
  };

  Array.from(document.getElementsByTagName('a')).forEach(handleLink);
};

handleTagLinks();

// Initial fetch of blog data when the script loads
fetchAndCacheBlogData(globalState, fetch, log, error);

setupAudio(
  document,
  getAudioElements,
  removeControlsAttribute,
  createElement,
  createTextNode,
  stopDefault,
  playAudio,
  pauseAudio,
  addEventListener,
  appendChild,
  insertBefore
);

/**
 * Creates an error handler for module loading errors
 * @param {string} modulePath - Path to the module that failed to load
 * @param {Function} errorFn - Error logging function
 * @returns {Function} Error handler function
 */
export function handleModuleError(modulePath, errorFn) {
  return (e) => {
    errorFn('Error loading module ' + modulePath + ':', e);
  };
}

/**
 * Creates a module initializer function that will be called when a dynamic import completes
 * @param {HTMLElement} article - The article element containing the toy
 * @param {string} functionName - The name of the exported function to use from the module
 * @param {object} globalState - The shared application state
 * @param {Function} createEnv - Function to create the environment map for the toy
 * @param {Function} error - Function for logging errors
 * @param {Function} fetch - Function for making HTTP requests
 * @param {object} dom - Object containing DOM functions
 * @returns {Function} A function that takes a module and initializes the interactive component
 */
export function initialiseModule(article, functionName, globalState, createEnv, error, fetch, dom) {
  return (module) => {
    const processingFunction = module[functionName];
    initializeInteractiveComponent(
      article,
      processingFunction,
      globalState,
      createEnv,
      error,
      fetch,
      dom
    );
  };
}

/**
 * Enable controls and update status message for an interactive component
 * @param {HTMLInputElement} inputElement
 * @param {HTMLButtonElement} submitButton
 * @param {HTMLElement} outputElement
 */
export function enableInteractiveControls(inputElement, submitButton, outputElement) {
  inputElement.disabled = false;
  submitButton.disabled = false;
  outputElement.textContent = 'Ready for input';
  outputElement.parentElement.classList.remove('warning');
}

function handleRequestResponse(url, outputElement, errorFn, fetchFn, dom) {
  fetchFn(url)
    .then(response => response.text())
    .then(body => {
      dom.setTextContent(outputElement, body);
    })
    .catch(fetchError => {
      errorFn('Error fetching request URL:', fetchError);
      dom.setTextContent(outputElement, 'Error fetching URL: ' + fetchError.message);
      dom.addWarningFn(outputElement);
    });
}

function handleParsedResult(parsed, outputElement, errorFn, fetchFn, dom) {
  if (parsed && typeof parsed === 'object' && parsed.request && typeof parsed.request.url === 'string') {
    handleRequestResponse(parsed.request.url, outputElement, errorFn, fetchFn, dom);
    return true;
  }
  return false;
}

/**
 * Parses the JSON result and returns the parsed object or null if parsing fails.
 * @param {string} result - The JSON string to parse.
 * @returns {object|null} The parsed object or null.
 */
function parseJSONResult(result) {
  try {
    return JSON.parse(result);
  } catch {
    return null;
  }
}

/**
 * Creates a submit handler function for an interactive toy.
 * @param {HTMLInputElement} inputElement - The input field.
 * @param {HTMLElement} outputElement - The element to display output/errors.
 * @param {HTMLElement} outputParent - The parent element of the output element.
 * @param {object} globalState - The shared application state.
 * @param {Function} processingFunction - The toy's core logic function.
 * @param {Function} stopDefault - Function to prevent default event action.
 * @param {Function} createEnv - Function to create the environment map for the toy.
 * @param {Function} errorFn - Function for logging errors.
 * @param {Function} addWarningFn - Function to add a warning style to the output.
 * @param {Function} fetchFn - Function to fetch data from a URL.
 * @param {Function} createElement - Function to create an element.
 * @param {Function} setTextContent - Function to set the text content of an element.
 * @returns {Function} An event handler function.
 */
export const createHandleSubmit = (inputElement, outputElement, outputParent, globalState, processingFunction, createEnv, errorFn, fetchFn, dom) => (event) => {
  if (event) {
    dom.stopDefault(event);
  }
  const inputValue = inputElement.value;

  try {
    const env = createEnv(globalState);

    // Call the processing function with the input value
    const result = processingFunction(inputValue, env);

    const parsed = parseJSONResult(result);

    if (!handleParsedResult(parsed, outputElement, errorFn, fetchFn, dom)) {
      dom.setTextContent(outputElement, result);
    }
  } catch (e) {
    errorFn('Error processing input:', e);
    dom.setTextContent(outputElement, 'Error: ' + e.message);
    dom.addWarningFn(outputElement);
  }
};

/**
 * Initializes the interactive elements (input, button, output) within a toy's article element.
 * Sets up event listeners and initial state.
 * @param {HTMLElement} article - The article element containing the toy.
 * @param {Function} processingFunction - The toy's core logic function.
 * @param {object} globalState - The shared application state.
 * @param {Function} createEnvFn - Function to create the environment map for the toy.
 * @param {Function} errorFn - Function for logging errors.
 * @param {Function} fetchFn - Function for making HTTP requests.
 * @param {object} dom - Object containing DOM functions.
 */
function disableInputAndButton(inputElement, submitButton) {
  inputElement.disabled = true;
  submitButton.disabled = true;
}

export function initializeInteractiveComponent(article, processingFunction, globalState, createEnvFn, errorFn, fetchFn, dom) {
  // Get the elements within the article
  const inputElement = dom.querySelector(article, 'input');
  const submitButton = dom.querySelector(article, 'button');
  const outputElement = dom.querySelector(article, 'p.output');
  const outputParent = dom.querySelector(article, 'div.output'); // Get the parent element

  // Disable input and submit during initialization
  disableInputAndButton(inputElement, submitButton);

  // Update message to show JS is running
  outputElement.textContent = 'Initialising...';

  // Create the submit handler using the function from this module
  const handleSubmit = createHandleSubmit(inputElement, outputElement, outputParent, globalState, processingFunction, createEnvFn, errorFn, fetchFn, dom);

  // Add event listener to the submit button
  dom.addEventListener(submitButton, 'click', handleSubmit);

  // Add event listener for Enter key in the input field
  dom.addEventListener(inputElement, 'keypress', createHandleKeyPress(handleSubmit));

  // Enable controls when initialization is complete using the function from this module
  enableInteractiveControls(inputElement, submitButton, outputElement);
}

/**
 * Returns a keypress event handler that triggers submit on Enter key.
 * @param {Function} handleSubmit - The submit handler function to call on Enter key.
 * @returns {Function} Keypress event handler.
 */
function createHandleKeyPress(handleSubmit) {
  return (event) => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  };
}

/**
 * Finds all interactive components registered on the window and sets up
 * IntersectionObservers (via the provided creator function) to lazy-load
 * and initialize them when they enter the viewport.
 * @param {Window} win - The window object.
 * @param {Document} doc - The document object.
 * @param {Function} logFn - Logging function.
 * @param {Function} warnFn - Warning function.
 * @param {Function} getElementByIdFn - Function to get element by ID.
 * @param {Function} createIntersectionObserverFn - Function that creates an IntersectionObserver for a given article, module path, and function name.
 */
export function initializeVisibleComponents(win, doc, logFn, warnFn, getElementByIdFn, createIntersectionObserverFn) {
  if (win.interactiveComponents && win.interactiveComponents.length > 0) {
    logFn('Initializing', win.interactiveComponents.length, 'interactive components via IntersectionObserver');
    win.interactiveComponents.forEach(component => {
      const article = getElementByIdFn(doc, component.id);
      if (article) {
        const observer = createIntersectionObserverFn(article, component.modulePath, component.functionName);
        observer.observe(article);
      } else {
        warnFn(`Could not find article element with ID: ${component.id} for component initialization.`);
      }
    });
  } else {
    warnFn('No interactive components found to initialize');
  }
}

/**
 * Helper function needed by getData
 */
export const getDeepStateCopy = (globalState) => JSON.parse(JSON.stringify(globalState));

#!/usr/bin/env node

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// Get __dirname equivalent in ES module scope
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Define base directories
const projectRoot = path.resolve(__dirname, '../..'); // Adjust based on script location
const srcDir = path.resolve(projectRoot, 'src');
const publicDir = path.resolve(projectRoot, 'public');
const srcToysDir = path.resolve(srcDir, 'toys');
const srcBrowserDir = path.resolve(srcDir, 'browser');

// Ensure public directory exists
if (!fs.existsSync(publicDir)) {
  fs.mkdirSync(publicDir, { recursive: true });
}

// --- Copy Toy Files --- 

// Function to recursively find JS files in the toys directory
function findJsFiles(dir) {
  let jsFiles = [];
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);
    if (entry.isDirectory()) {
      jsFiles = jsFiles.concat(findJsFiles(fullPath));
    } else if (entry.isFile() && entry.name.endsWith('.js') && !entry.name.endsWith('.test.js')) {
      jsFiles.push(fullPath);
    }
  }
  return jsFiles;
}

// Find all JS files in src/toys
const toyFiles = findJsFiles(srcToysDir);

// Copy each toy file to the corresponding path in public
toyFiles.forEach(filePath => {
  const relativePath = path.relative(srcToysDir, filePath);
  const destPath = path.join(publicDir, relativePath);
  const destDir = path.dirname(destPath);

  // Ensure the destination directory exists
  if (!fs.existsSync(destDir)) {
    fs.mkdirSync(destDir, { recursive: true });
  }

  // Copy the file
  fs.copyFileSync(filePath, destPath);
  console.log(`Copied: ${filePath} -> ${destPath}`);
});

console.log('Toy files copied successfully!');

// --- Copy Specific Assets --- 

const assetsToCopy = [
  { src: path.join(srcDir, 'blog.json'), dest: path.join(publicDir, 'blog.json') },
  { src: path.join(srcBrowserDir, 'data.js'), dest: path.join(publicDir, 'data.js') },
  { src: path.join(srcBrowserDir, 'main.js'), dest: path.join(publicDir, 'main.js') },
  { src: path.join(srcBrowserDir, 'toys.js'), dest: path.join(publicDir, 'toys.js') },
  { src: path.join(srcBrowserDir, 'audio-controls.js'), dest: path.join(publicDir, 'audio-controls.js') },
  { src: path.join(srcBrowserDir, 'document.js'), dest: path.join(publicDir, 'document.js') },
  { src: path.join(srcBrowserDir, 'tags.js'), dest: path.join(publicDir, 'tags.js') }
];

assetsToCopy.forEach(asset => {
  if (fs.existsSync(asset.src)) {
    fs.copyFileSync(asset.src, asset.dest);
    console.log(`Copied: ${asset.src} -> ${asset.dest}`);
  } else {
    console.warn(`Warning: Asset not found, skipping copy: ${asset.src}`);
  }
});

console.log('Specific assets copied successfully!');

function buildDecomposedResult(num) {
  const A = formatDecimal(num);
  const decomposition = getIEEEDecomposition(num);
  if (!decomposition) return "";
  return formatFloatDecomposition(A, decomposition);
}

export function decomposeFloat(input) {
  const num = Number(input);
  const simple = handleSimpleCases(num);
  if (simple !== null) return simple;
  return buildDecomposedResult(num);
}

function getZeroVariantResult(num) {
  const result = isZeroVariant(num);
  return result !== null ? result : null;
}

function getIEEEDecomposition(num) {
  const parts = decomposeIEEE754(num);
  if (!isValidIEEEParts(parts)) return null;
  return getSignificandAndExponent(parts);
}

function formatFloatDecomposition(decimal, { B, C }) {
  return `${decimal} (${B.toString()} × 2^${C.toString()})`;
}

function getZeroVariantString(num) {
  return getZeroVariantResult(num);
}

function resolveZeroVariant(num) {
  const zeroResult = getZeroVariantString(num);
  return zeroResult ? zeroResult : null;
}

function handleSimpleCases(num) {
  if (!Number.isFinite(num)) return "";
  if (resolveZeroVariant(num)) return resolveZeroVariant(num);
  return null;
}

function isPositiveZero(n) {
  return Object.is(n, 0);
}

function isNegativeZero(n) {
  return Object.is(n, -0);
}

function isPositiveZeroResult(num) {
  return isPositiveZero(num) ? "0 (0 × 2^0)" : null;
}

function isNegativeZeroResult(num) {
  return isNegativeZero(num) ? "0 (-0 × 2^0)" : null;
}

function isZeroVariant(num) {
  return isPositiveZeroResult(num) || isNegativeZeroResult(num);
}

function formatDecimal(num) {
  let A = num.toPrecision(17);
  return A.includes('.') ? A.replace(/\.?0+$/, '') : A;
}

function hasIEEEFields(parts) {
  return ['sign', 'mantissa', 'exponent'].every(key => key in parts);
}

function isValidIEEEParts(parts) {
  return Boolean(parts) && hasIEEEFields(parts);
}

function getSignificandAndExponent({ sign, mantissa, exponent }) {
  const signValue = sign === 0 ? 1n : -1n;
  const fullSignificand = (1n << 52n) | BigInt(mantissa);
  const B = signValue * fullSignificand;
  const C = BigInt(exponent - 1023) - 52n;
  return { B, C };
}

function decomposeIEEE754(value) {
  if (!Number.isFinite(value)) return {};

  const bits = getFloat64Bits(value);
  return extractIEEEComponents(bits);
}

function getFloat64Bits(value) {
  const buffer = new ArrayBuffer(8);
  const floatView = new Float64Array(buffer);
  const byteView = new Uint8Array(buffer);

  floatView[0] = value;

  let bits = 0n;
  for (let i = 7; i >= 0; i--) {
    bits = (bits << 8n) | BigInt(byteView[i]);
  }

  return bits;
}

function extractIEEEComponents(bits) {
  const sign = Number((bits >> 63n) & 1n);
  const exponentBits = (bits >> 52n) & 0x7FFn;
  const mantissaBits = bits & 0xFFFFFFFFFFFFFn;

  if (exponentBits === 0n) {
    return {};
  }

  return {
    sign,
    mantissa: Number(mantissaBits),
    exponent: Number(exponentBits),
  };
}

function isRecognizedBait(baitKey, baitOptions) {
  return baitKey in baitOptions;
}

function isEmptyBait(baitKey) {
  return baitKey.length === 0;
}

function getDefaultBaitResponse(moodDescription) {
  return {
    isError: true,
    message: `You cast your line with nothing but hesitation. Without any bait, the waters remain undisturbed in their ${moodDescription}.`
  };
}

function getRecognizedBait(baitKey, baitOptions) {
  return baitOptions[baitKey];
}

function getUnrecognizedBait() {
  return { modifier: 0, description: "an unconventional bait" };
}

function getBaitData(input, baitOptions, moodDescription) {
  const baitKey = input.trim().toLowerCase();
  const recognized = isRecognizedBait(baitKey, baitOptions);
  if (recognized) return getRecognizedBait(baitKey, baitOptions);
  if (isEmptyBait(baitKey)) return getDefaultBaitResponse(moodDescription);
  return getUnrecognizedBait();
}

function getTimeOfDay(hour) {
  const ranges = [
    { start: 5, end: 12, label: "morning" },
    { start: 12, end: 17, label: "afternoon" },
    { start: 17, end: 21, label: "evening" },
    { start: 21, end: 24, label: "night" },
    { start: 0, end: 5, label: "night" },
  ];
  const match = ranges.find(({ start, end }) => hour >= start && hour < end);
  if (!match) throw new Error(`Unrecognized hour: ${hour}`);
  return match.label;
}

function getSeason(month) {
  const ranges = [
    { months: [11, 0, 1], label: "winter" },
    { months: [2, 3, 4], label: "spring" },
    { months: [5, 6, 7], label: "summer" },
    { months: [8, 9, 10], label: "fall" },
  ];
  const match = ranges.find(({ months }) => months.includes(month));
  if (!match) throw new Error(`Unrecognized month: ${month}`);
  return match.label;
}

function getMoodDescription(season, timeOfDay) {
  const seasonDescriptions = {
    winter: "crisp, icy waters",
    spring: "bubbling, fresh currents",
    summer: "warm, shimmering waves",
    fall: "cool, reflective ponds",
  };
  const timeDescriptions = {
    morning: "as dawn breaks with promise",
    afternoon: "under a vibrant sun",
    evening: "in the glow of twilight",
    night: "beneath a silent, starry sky",
  };
  return `${seasonDescriptions[season]} ${timeDescriptions[timeOfDay]}`;
}

function isSilentCatch(chance) {
  return chance < 0.3;
}

function isCommonCatch(chance) {
  return chance < 0.6;
}

function isTroutCatch(chance) {
  return chance < 0.85;
}

const fishingOutcomes = [
  {
    check: isSilentCatch,
    describe: (bait, mood) =>
      `the water stays silent. Despite your use of ${bait}, no fish disturb the ${mood}.`,
  },
  {
    check: isCommonCatch,
    describe: (bait, mood) =>
      `a common carp surfaces gently, a modest reward for your effort with ${bait}, set against ${mood}.`,
  },
  {
    check: isTroutCatch,
    describe: (bait, mood) =>
      `a glimmering trout appears briefly, its shimmer echoing the beauty of ${mood}. Your choice of ${bait} worked well.`,
  },
  {
    check: () => true,
    describe: (bait, mood) =>
      `in a burst of brilliance, a legendary golden fish leaps forth—its radiance matching the splendor of ${mood}. Your ${bait} has yielded a prize.`,
  },
];

function getFishingOutcome(effectiveChance, baitDescription, moodDescription) {
  return fishingOutcomes.find(({ check }) => check(effectiveChance)).describe(baitDescription, moodDescription);
}

function getBaitOptions() {
  return {
    "worm": { modifier: 0.0, description: "a wriggling worm" },
    "insect": { modifier: 0.05, description: "a lively insect" },
    "bread": { modifier: -0.05, description: "a slice of bread" },
    "cheese": { modifier: 0.1, description: "a pungent piece of cheese" },
    "shiny bait": { modifier: 0.15, description: "a glittering lure" },
    "doughnut": { modifier: 0.2, description: "a tempting doughnut" },
    "grub": { modifier: 0.05, description: "a succulent grub" },
    "minnow": { modifier: 0.1, description: "a darting minnow" },
    "sausage": { modifier: 0.2, description: "a savory sausage" },
    "maggot": { modifier: -0.1, description: "a squirming maggot" },
  };
}

function getTimeContext(getCurrentTime) {
  const date = new Date(getCurrentTime());
  const month = date.getMonth();
  const hour = date.getHours();
  const season = getSeason(month);
  const timeOfDay = getTimeOfDay(hour);
  return { season, timeOfDay };
}

function fishingGame(input, env) {
  const { season, timeOfDay } = getTimeContext(env.get("getCurrentTime"));

  const moodDescription = getMoodDescription(season, timeOfDay);

  const baitOptions = getBaitOptions();

  const baitDataOrError = getBaitData(input, baitOptions, moodDescription);
  if (baitDataOrError.isError) return baitDataOrError.message;
  const baitData = baitDataOrError;

  const getRandomNumber = env.get("getRandomNumber");
  const baseChance = getRandomNumber();
  const effectiveChance = Math.min(1, Math.max(0, baseChance + baitData.modifier));

  const outcome = getFishingOutcome(effectiveChance, baitData.description, moodDescription);

  return `Casting your line with ${baitData.description}, you await a catch. ${outcome}`;
}

export { fishingGame };

/**
 * Retrieves a value from data provided by the environment using the input as a path.
 * @param {string} input - The path (e.g., 'key1.key2.0.key3') to look up in the data.
 * @param {Map<string, Function>} env - Environment map containing dependencies. Expected: 'getData'.
 * @returns {string} The JSON stringified value found at the path, or an error message.
 */
export function get(input, env) {
  if (!env || typeof env.get !== 'function') {
    return "Error: 'env' Map with 'get' method is required.";
  }
  const getData = env.get('getData');
  if (typeof getData !== 'function') {
    return "Error: 'getData' function not found in env.";
  }

  try {
    const data = getData();
    if (input.trim() === '') {
      return JSON.stringify(data);
    }
    // Basic check if initial data is an object or array
    if (data === null || (typeof data !== 'object' && !Array.isArray(data))) {
        return "Error: 'getData' did not return a valid object or array.";
    }

    // Split the input path by dots
    const pathSegments = input.split('.');
    let currentValue = data;
    let currentPath = '';

    // Traverse the path
    for (const segment of pathSegments) {
      currentPath = currentPath ? `${currentPath}.${segment}` : segment;
      if (currentValue === null || typeof currentValue !== 'object') {
        return `Error: Cannot access property '${segment}' on non-object value at path '${currentPath.substring(0, currentPath.lastIndexOf('.') !== -1 ? currentPath.lastIndexOf('.') : 0)}'. Value is: ${JSON.stringify(currentValue)}`;
      }

      if (Object.prototype.hasOwnProperty.call(currentValue, segment)) {
        currentValue = currentValue[segment];
      } else {
        return `Error: Path segment '${segment}' not found at '${currentPath}'. Available keys/indices: ${Object.keys(currentValue).join(', ')}`;
      }
    }

    // Return a string representation of the final value
    try {
      return JSON.stringify(currentValue);
    } catch (stringifyError) {
        return `Error stringifying final value at path "${input}": ${stringifyError.message}`;
    }
  } catch (error) {
      // Catch errors from getData() execution or other unexpected issues
      return `Error during data retrieval or path traversal for "${input}": ${error.message}`;
  }
}

/**
 * Checks if an item is a plain object (not an array or null).
 * @param {*} item - The item to check.
 * @returns {boolean}
 */
function isObject(item) {
  return (item && typeof item === 'object' && !Array.isArray(item));
}

/**
 * Deeply merges two objects. Creates a new object with merged properties.
 * Properties in source will overwrite properties in target, unless both
 * properties are plain objects, in which case they are recursively merged.
 * Arrays and other types are overwritten, not merged.
 * @param {object} target - The target object.
 * @param {object} source - The source object.
 * @returns {object} A new object representing the merged result.
 */
function deepMerge(target, source) {
  const output = { ...target }; // Start with a shallow copy of the target
  if (isObject(target) && isObject(source)) {
    Object.keys(source).forEach(key => {
      const targetValue = target[key];
      const sourceValue = source[key];
      if (isObject(targetValue) && isObject(sourceValue)) {
        // If both target and source values are objects, recursively merge
        output[key] = deepMerge(targetValue, sourceValue);
      } else {
        // Otherwise, overwrite with the source value
        // (If sourceValue is an object, this assignment handles the case
        // where target[key] wasn't an object or didn't exist)
        output[key] = sourceValue;
      }
    });
  } else if (isObject(source)) {
      // If target is not an object but source is, return a shallow copy of source
      // (or deep copy if required, but shallow should suffice here as we merge onto it)
      return { ...source };
  }
  // If source is not an object, the initial shallow copy of target is returned
  // or target itself if it wasn't an object either (though initial checks prevent this)
  return output;
}

/**
 * Parses input as JSON, deep merges it into the 'temporary' object obtained via env.getData(),
 * and then passes the entire modified data structure back to env.setData().
 * @param {string} input - A JSON string to parse and merge.
 * @param {Map<string, Function>} env - Environment map. Expected: 'getData()', 'setData(data)'.
 * @returns {string} A confirmation message or an error message.
 */
export function setTemporary(input, env) {
  let inputJson;
  try {
    inputJson = JSON.parse(input);
  } catch (parseError) {
    return `Error: Invalid JSON input. ${parseError.message}`;
  }

  if (!isObject(inputJson)) { // Use the helper
      return "Error: Input JSON must be a plain object.";
  }

  if (!env || typeof env.get !== 'function') {
    return "Error: 'env' Map with 'get' method is required.";
  }
  const getData = env.get('getData');
  const setData = env.get('setData');
  if (typeof getData !== 'function') {
    return "Error: 'getData' function not found in env.";
  }
  if (typeof setData !== 'function') {
      return "Error: 'setData' function not found in env.";
  }

  try {
    const currentData = getData(); 

    if (!isObject(currentData)) { // Use the helper
        return "Error: 'getData' did not return a valid object.";
    }

    // Deep clone currentData to create newData - JSON method is simple but has limitations (e.g., with Dates, Functions)
    // For this use case, it should be acceptable.
    const newData = JSON.parse(JSON.stringify(currentData));

    // Ensure the 'temporary' key exists and is an object in the new copy
    if (!isObject(newData.temporary)) {
        newData.temporary = {}; 
    }

    // Perform the deep merge
    newData.temporary = deepMerge(newData.temporary, inputJson);

    setData(newData);

    return `Success: Temporary data deep merged.`; 

  } catch (error) {
    return `Error updating temporary data: ${error.message}`;
  }
}

function handleHackerDoor(lowerInput, nextInventory, nextVisited) {
  if (lowerInput.includes("zero")) {
    const output = `> Password accepted. Inside, a rogue AI offers you a cracked implant.`;
    nextInventory.push("cracked implant");
    nextVisited.add("hacker");
    return { output, nextState: "hub", nextInventory, nextVisited };
  } else {
    return { output: `> Hint: the password is a number and a name...`, nextState: "hacker:door", nextInventory, nextVisited };
  }
}

export function cyberpunkAdventure(input, env) {
  try {
    const getRandomNumber = env.get("getRandomNumber");
    const getCurrentTime = env.get("getCurrentTime");
    const getData = env.get("getData");
    const setTemporaryData = env.get("setData");
    const { temporary } = getData();
    const scoped = temporary.CYBE1 || {};

    const name = scoped.name || input.trim() || "Stray";
    const state = scoped.state || "intro";
    const inventory = scoped.inventory || [];
    const visited = new Set(scoped.visited || []);

    const lowerInput = input.trim().toLowerCase();
    const time = getCurrentTime();

    let output = "";
    let nextState = state;
    let nextInventory = [...inventory];
    let nextVisited = new Set(visited);

    // If we haven’t stored the name yet, do so now
    if (!scoped.name) {
      setTemporaryData({ temporary: { CYBE1: { name } } });
      return `> Welcome, ${name}. Your story begins now.\n> Type 'start' to continue.`;
    }

    // Game logic
    switch (state) {
      case "intro":
        output = `> ${time}\n> ${name}, you're in the Neon Market. Lights hum. Faces blur.\n> You see paths to: Hacker Den, Transport Hub, and Back Alley.\n> Where do you go? (hacker / transport / alley)`;
        nextState = "hub";
        break;

      case "hub":
        if (lowerInput.includes("hacker")) {
          output = `> You approach the Hacker Den. The door requires a password.`;
          nextState = "hacker:door";
        } else if (lowerInput.includes("transport")) {
          output = `> You head to the Transport Hub. Trains screech overhead.`;
          nextState = "transport:platform";
        } else if (lowerInput.includes("alley")) {
          output = `> You slip into the Back Alley. The shadows move with you.`;
          nextState = "alley:stealth";
        } else {
          output = `> Unclear direction. Options: hacker / transport / alley`;
        }
        break;

      // Hacker Den phases
      case "hacker:door": {
        const result = handleHackerDoor(lowerInput, nextInventory, nextVisited);
        output = result.output;
        nextState = result.nextState;
        nextInventory = result.nextInventory;
        nextVisited = result.nextVisited;
        break;
      }

      // Transport Hub phases
      case "transport:platform":
        output = `> A vendor offers you a neural ticket in exchange for your datapad.`;
        nextState = "transport:trade";
        break;

      case "transport:trade":
        if (nextInventory.includes("datapad") && lowerInput.includes("trade")) {
          output = `> You hand over the datapad. The vendor grins and slips you the neural ticket.`;
          nextInventory.splice(nextInventory.indexOf("datapad"), 1);
          nextInventory.push("neural ticket");
          nextVisited.add("transport");
          nextState = "hub";
        } else {
          output = `> Do you want to trade? Type 'trade datapad'.`;
        }
        break;

      // Back Alley phases
      case "alley:stealth":
        const stealthCheck = getRandomNumber();
        const success = stealthCheck > 0.3;
        if (success) {
          output = `> You dodge the shadows and find a hidden stash: a stimpack.`;
          nextInventory.push("stimpack");
          nextVisited.add("alley");
        } else {
          output = `> You trip a wire. Sirens start up. You sprint back to the Market.`;
        }
        nextState = "hub";
        break;

      default:
        output = `> Glitch in the grid. Resetting...`;
        nextState = "intro";
    }

    setTemporaryData({
      temporary: {
        CYBE1: {
          name,
          state: nextState,
          inventory: nextInventory,
          visited: [...nextVisited]
        }
      }
    });

    return output;
  } catch {
    return `> SYSTEM ERROR: neural link failure`;
  }
}

function getOpponent(player) {
  return player === "X" ? "O" : "X";
}

function tryParseJSON(input) {
  try {
    return JSON.parse(input);
  } catch {
    return null;
  }
}

function respectsTurnOrder(index, player, moves) {
  return index === 0 || player !== moves[index - 1].player;
}

function validateParsedMoves(parsed) {
  return parsed && typeof parsed === "object" && Array.isArray(parsed.moves)
    ? parsed.moves
    : null;
}

function parseInputSafely(input) {
  const parsed = tryParseJSON(input);
  return validateParsedMoves(parsed);
}

function shouldSkipMove(earlyWin, moves) {
  return earlyWin || moves.length >= 9;
}

function buildMoveResponse(moves, newMove = null) {
  const updatedMoves = newMove ? [...moves, newMove] : moves;
  return JSON.stringify({ moves: updatedMoves });
}

export function ticTacToeMove(input) {
  const moves = parseInputSafely(input);
  if (!moves) return returnInitialOptimalMove();

  const result = validateAndApplyMoves(moves);
  if (!result) return returnInitialOptimalMove();
  const { board, earlyWin } = result;
  if (shouldSkipMove(earlyWin, moves)) return buildMoveResponse(moves);

  const nextPlayer = determineNextPlayer(moves);
  const bestMove = findBestMove(board, nextPlayer, moves);
  const newMove = { player: nextPlayer, position: bestMove };
  return buildMoveResponse(moves, newMove);
}

function initializeBoardAndSeen() {
  const board = Array.from({ length: 3 }, () => Array(3).fill(null));
  const seen = new Set();
  return { board, seen };
}

function processMove(move, index, moves, board, seen) {
  if (!isValidMove(move, index, moves)) return false;
  if (!applyMoveToBoard(board, move, seen)) return false;
  return true;
}

function validateAndApplyMoves(moves) {
  if (!Array.isArray(moves) || moves.length > 9) return null;

  const { board, seen } = initializeBoardAndSeen();

  for (let i = 0; i < moves.length; i++) {
    const move = moves[i];
    if (!processMove(move, i, moves, board, seen)) return null;

    if (checkEarlyWin(board)) {
      return { board, earlyWin: true };
    }
  }

  return { board, earlyWin: false };
}

function scoreMove(board, r, c, player, moves) {
  board[r][c] = player;
  const score = minimax(board, 0, false, player, moves);
  board[r][c] = null;
  return score;
}

function findBestMove(board, nextPlayer, moves) {
  let best = -Infinity;
  let bestMove = null;

  for (let r = 0; r < 3; r++) {
    for (let c = 0; c < 3; c++) {
      if (!board[r][c]) {
        const score = scoreMove(board, r, c, nextPlayer, moves);
        if (score > best) {
          best = score;
          bestMove = { row: r, column: c };
        }
      }
    }
  }

  return bestMove;
}

function determineNextPlayer(moves) {
  if (moves.length === 0) return "X";
  return moves[moves.length - 1].player === "X" ? "O" : "X";
}

function checkRows(board, player) {
  for (let i = 0; i < 3; i++) {
    if (board[i][0] === player && board[i][1] === player && board[i][2] === player) {
      return true;
    }
  }
  return false;
}

function checkColumns(board, player) {
  for (let i = 0; i < 3; i++) {
    if (board[0][i] === player && board[1][i] === player && board[2][i] === player) {
      return true;
    }
  }
  return false;
}

function checkDiagonals(board, player) {
  return (
    (board[0][0] === player && board[1][1] === player && board[2][2] === player) ||
    (board[0][2] === player && board[1][1] === player && board[2][0] === player)
  );
}

function checkEarlyWin(board) {
  return isWin(board, "X") || isWin(board, "O");
}

function applyMoveToBoard(board, move, seen) {
  const { player, position } = move;
  const { row, column } = position;

  const key = `${row},${column}`;
  if (seen.has(key)) return false;
  seen.add(key);

  board[row][column] = player;
  return true;
}

function evaluateTerminalState(board, player, opponent, depth, moves) {
  if (isWin(board, player)) return 10 - depth;
  if (isWin(board, opponent)) return depth - 10;
  if (depth + moves.length === 9) return 0;
  return null;
}

function getAvailableMoves(board) {
  const moves = [];
  for (let r = 0; r < 3; r++) {
    for (let c = 0; c < 3; c++) {
      if (!board[r][c]) moves.push([r, c]);
    }
  }
  return moves;
}

function simulateMoves(board, depth, isMax, player, moves) {
  const scores = [];
  const opponent = getOpponent(player);
  for (const [r, c] of getAvailableMoves(board)) {
    board[r][c] = isMax ? player : opponent;
    scores.push(minimax(board, depth + 1, !isMax, player, moves));
    board[r][c] = null;
  }
  return scores;
}

function minimax(board, depth, isMax, player, moves) {
  const opponent = getOpponent(player);
  const terminalScore = evaluateTerminalState(board, player, opponent, depth, moves);
  if (terminalScore !== null) return terminalScore;

  const scores = simulateMoves(board, depth, isMax, player, moves);
  return isMax ? Math.max(...scores) : Math.min(...scores);
}

function hasValidPlayer(player) {
  return ["X", "O"].includes(player);
}

function hasValidPosition(position) {
  if (!position || typeof position !== "object") return false;
  const { row, column } = position;
  return [0, 1, 2].includes(row) && [0, 1, 2].includes(column);
}

function isValidMove(move, index, moves) {
  if (!move || typeof move !== "object") return false;

  const { player, position } = move;
  if (!hasValidPlayer(player)) return false;
  if (!hasValidPosition(position)) return false;

  if (!respectsTurnOrder(index, player, moves)) return false;

  return true;
}

function isWin(board, player) {
  return checkRows(board, player) || checkColumns(board, player) || checkDiagonals(board, player);
}

function returnInitialOptimalMove() {
  // In an empty board, the optimal first move is the center
  const optimal = { player: "X", position: { row: 1, column: 1 } };
  return JSON.stringify({ moves: [optimal] });
}

